css:
@-moz-document regexp("https?://steamcommunity.com/tradeoffer/.*"), regexp("https?://steamcommunity.com/trade/.*") {
/* .itemholder {width:30px} */


#trade_area {
    width: 1110px!important;
    margin: 0 auto
}
.trade_box {
    width: 426px!important
}
#inventories {
    overflow: hidden
}
#appselect,
.filter_ctn,
.trade_partner_header,
.trade_partner_header div {
    margin-bottom: 0;
    padding-bottom: 0
}
.trade_left,
#inventory_box,
#tradeoffer_addmessage .log {
    width: 290px!important
}
#tradeoffer_addmessage .log_chat_entry_area {
    padding: 2px
}
#trade_offer_note_form {
    width: 277px
}
#tradeoffer_addmessage .log,
#trade_offer_note_form {
    height: 50px
}
#trade_offer_note {
    height: 80%!important
}
.trade_left .trade_item_box,
.inventory_ctn,
.inventory_page {
    width: 268px!important;
    line-height: normal!important;
    height: 267px!important
}
/* .tutorial_arrow_ctn {position:absolute;top:0px;right:0px} */

#appselect {
    height: 16px;
    width: 280px
}
#appselect img {
    width: 12px;
    height: 12px
}
#appselect_activeapp {
    line-height: normal;
    padding: 0;
    margin: 0
}
#appselect_arrow {
    height: 20px!important;
    line-height: normal;
    background-image: none!important;
    background: #666
}
#appselect_arrow::after {
    content: " >";
    color: white;
    padding-left: 3px
}
#filter_tag_show > span {
    display: none
}
#filter_tag_show::before {
    content: "filters ?";
    padding: 1px 5px 1px 5px;
    font-size: 12px
}
.trade_box .itemHolder {
    margin-right: 3px!important;
    margin-bottom: 3px
}
.trade_item_box {
    padding: 2px
}
.itemHolder,
.slot_inner,
.itemHolder .item,
.itemHolder .item_throbber,
.itemHolder .item img {
    width: 64px!important;
    height: 64px!important;
    margin-top: 0
}
.inventory_user_tabs,
.inventory_user_tab div,
.inventory_user_tab {
    height: 24px!important;
    line-height: normal!important;
    font-size: 16px
}
.inventory_user_tab div,
.inventory_user_tab {
    width: 135px!important;
    overflow: hidden
}
.filter_right_controls.filter_tag_button_ctn {
    width: auto
}
.filter_right_controls {
    width: 170px;
    margin-top: 4px
}
/* trade only, not offer */

#filter_tag_show {
    padding: 0;
    height: 18px
}
#filter_tag_show span {
    font-size: 10px
}
.trade_box_contents {
    padding: 0
}
.filter_control_ctn.filter_tag_button_ctn {
    padding: 0;
    margin-bottom: 4px
}
/* trade only, not offer */

.item_adder::before {
    content: "x"
}
#amount_control {
    width: 32px
}
#filter_control {
    width: 100px
}
#filter_clear_btn {
    left: 100px;
    bottom: 4px;
    top: auto;
    right: auto;
    color: #b00;
    background: #111;
    width: auto
}
#filter_clear_btn::after {
    content: "x"
}
.item_adder .selectableNone,
.filter_ctn .selectableNone,
#tradeoffer_addmessage h2,
#inventory_displaycontrols,
.trade_rule {
    display: none!important
}
.trade_right,
#trade_area {
    width: 705px!important;
    height: 900px!important
}
#mainContent {
    width: 100%!important
}
div.trade_left {
    position: absolute!important;
    left: 0px;
    margin-right: -50px!important
}
div.trade_right {
    position: absolute!important;
    left: 292px!important;
    margin-right: 0px!important
}
.trade_right #your_slots .itemHolder {
    counter-increment: your
}
.trade_right #their_slots .itemHolder {
    counter-increment: their
}
.trade_right .itemHolder.trade_slot:nth-child(5n) {
    border-right: 3px solid #000;
}
.trade_right .itemHolder.trade_slot:nth-child(10n) {
    border-right: 24px solid #111;
}
.trade_right .itemHolder.trade_slot:nth-child(50n) {
    margin-bottom: 5px
}
.trade_right .itemHolder.trade_slot:nth-child(100n) {
    border-right: 6px solid red;
}
.trade_right .itemHolder.trade_slot::after {
    color: white;
    z-index: 99;
    position: relative;
    top: -50px;
    left: 68px;
    font-size: 10px
}
.trade_right #your_slots .itemHolder.trade_slot:nth-child(10n)::after {
    content: counter(your)
}
.trade_right #their_slots .itemHolder.trade_slot:nth-child(10n)::after {
    content: counter(their)
}
.log,
.log .trade_box_contents {
    width: 280px!important
}
#footer {
    display: none
}
a.inventory_user_tab > div { opacity:0.1 }
a.inventory_user_tab.active > div {  opacity:1 }
}

@-moz-document regexp("https?://steamcommunity.com/.*/tradeoffers.*") {
.content {
    height: auto!important
}
.header_installsteam_btn,
.header_installsteam_btn_gray,
.header_installsteam_btn_leftcap,
.header_installsteam_btn_content {
    display: none
}
.maincontent {
    width: 99%!important
}
.profile_leftcol {
    width: calc(99% - 160px)
}
.tradeoffer {
    margin-bottom: 4px;
    padding-top: 12px
}
.tradeoffer_items_ctn {
    padding: 2px
}
.tradeoffer_header,
.tradeoffer_message {
    margin-bottom: 0
}
.tradeoffer_message .quote {
    line-height: normal;
    padding: 1px 1px 1px 16px
}
.btn_report {
    margin-top: 0!important;
    background: transparent
}
.btn_report span {
    line-height: normal
}
.btn_report .report {
    margin: 0
}
/*.tradeoffer_item_list .trade_item {counter-increment:n}
.tradeoffer_item_list .trade_item:nth-child(5n)::after {color:white;z-index:99;position:absolute;font-size:12px!important;right:0px;top:0px;font-size:10px;content:counter(n);background:black}
*/

.trade_item {
    margin-right: 3px!important;
    margin-bottom: 3px!important
}
.profile_small_header_bg,
.tradeoffer_items_rule,
.tradeoffer_items_avatar_ctn,
.tradeoffer_items_header,
.tradeoffer_rule,
.tradeoffer_message .tooltip_hint {
    display: none
}
.profile_rightcol {
    width: 150px
}
.right_controls_large_block_active_bg,
.right_controls_secondary_active_bg {
    width: 140px;
    top: 0px
}
.rightcol_controls_content .btn_medium span {
    line-height: normal
}
}

@-moz-document regexp("https?://steamcommunity.com/.*/tradeoffers/*") {
/* counters only on the main offer page -- too slow for history page */

.tradeoffer_item_list .trade_item {
    counter-increment: n
}
.tradeoffer_item_list .trade_item::after {
    color: white;
    z-index: 99;
    position: absolute;
    font-size: 12px;
    right: 0px;
    top: 0px;
    border-radius: 4px;
}
.tradeoffer_item_list .trade_item:nth-child(5n)::after {
    content: counter(n);
    background: black
}
.tradeoffer_item_list .trade_item:not(:nth-child(-n+4)):nth-last-child(2)::after {
    content: counter(n);
    background: #141;
}
}
