css:
@-moz-document domain("forum.thd.vg") {
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 400;
  src: local('Source Sans Pro'), local('SourceSansPro-Regular'), url(https://fonts.gstatic.com/s/sourcesanspro/v9/ODelI1aHBYDBqgeIAH2zlNV_2ngZ8dMf8fLgjYEouxg.woff2) format('woff2');
}

body {
font-family: 'Source Sans Pro', sans-serif;
    line-height: 1.10;
}

html {
    background: #3E2E11 url('https://i.imgur.com/6qZYGyg.png') no-repeat;
}

body #moderatorBar {    
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    border: 1;
 	background-color: #353535;   
    border-left-style: solid;
    border-bottom-style: none;
    border-right-style: solid;
    border-width: 4px;
    border-color: #242424;
    -moz-box-shadow:inset 1px 0 #242424;
    box-shadow:inset 0.5px 0 #242424;
    
 	max-width: 1200px;     
    display: block;
    margin-left: auto;
    margin-right: auto;
}

#headerMover {
 	max-width: 1200px;     
    display: block;
    margin-left: auto;
    margin-right: auto;
}

#header {
	border-bottom-left-radius: 8px;
  	border-bottom-right-radius: 8px;
}

#logoBlock {
    border-right-style: solid;
    border-left-style: solid;
    border-color: #222222;
    border-width: 4px;
    
  	background: -webkit-linear-gradient(#353535, #242424);
  	background: -o-linear-gradient(#353535, #242424);
  	background: -moz-linear-gradient(#353535, #242424);
  	background: linear-gradient(#353535, #242424);
}

.navTabs .navTab.selected .tabLinks {
	border-top-left-radius: 5px;
  	border-top-right-radius: 5px;
}

.navTabs, .nodeInfo.categoryNodeInfo.categoryStrip {
    background-color: #242424;   
}

.titleBar {
    display: none;
}

#logo {
    position: relative;
    left: 1%;
}

#logo .logo_text {
	font-size: 20pt;
}

.pageWidth {
    max-width: 1200px;
}


.nodeDescription {
 	display: none;   
}

a:link, a:visited {
 	color: #772233;   
}

.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt, body .muted, body a.muted, body .muted a, .sidebar .userList .username.invisible, .sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeadin, .sidebar .avatarList .userTitle, .messageUserBlock .userTitle, .threadListItem .title, .threadListItem .additionalRow {
    color: #8C9CA3;
}

div.showModeratorsMessage {
 	display: none;   
}
}
