css:
@-moz-document url-prefix("http://your.site.com/") {
body
{
    background: #141414;
    color: #808080;
}
.CodeMirror
{
    background: #292929;
    color: #808080;
    border-color: #292929;
}
.CodeMirror-gutters
{
    background: #363636;
    border-color: #363636;
    
}
button
{
    background: #0D0D0D;
    color: #D1D1D1;
    border-radius: 5px;
    border-color: #0D0D0D;
    margin-left: 5px;
}
select
{
    background: #0D0D0D;
    color: #D1D1D1;
    border-radius: 2px;
    border-color: #0D0D0D;
}
input
{
    background: #0D0D0D;
    color: #D1D1D1;
    border-radius: 5px;
    border-color: #0D0D0D;
    
}
#header
{
	-webkit-box-shadow: 0 0 3rem -1.2rem white;
}



}
