css:
@-moz-document url-prefix("http://anichart.net") {
#container-main {
  margin-right: 3.5px
}
a {
  text-shadow: 1px 1px 1px #00ACED;
}
body {
  background-color: #2A2A2A;
  text-shadow: 0.5px 0.5px 0.5px rgba(0, 255, 255, 0.5);
}
.card {
  background-color: #222;
}
.card__count {
  color: #00ACED;
  background-color: #191919;
  border-bottom: #191919;
}
.card__count::after {
  background: #191919;
  color: #0FF;
}
.cal-card {
  background-color: #111;
}
.cal-time {
  color: #0FF;
}
.cal-day h2 {
  color: #0FF;
}
.cal-time timer {
  color: #00F;
  text-shadow: none;
}
.card__links, .cal-links {
  background-color: #1F1F1F;
  border-top: none;
}
.card__title {
  color: #00ACED;
  border-bottom: none;
}
.card__title a:last-of-type{
  color: #0033CF;
}
.card__studio {
  color: #00ACED;
}
.card--sml .card--sml__title {
  color: #00ACED;
}
.card .card__default p {
  color: #00FFFF;
  background-color: #191919;
  border-bottom: #191919;
}
.card .card__default p:last-of-type {
  color: #00FFFF;
  background-color: #191919;
  border-left: #191919;
}
.card--sml .card--sml__title div:last-of-type {
  color: #0FF;
}
.des {
  color: #00AAFF;
}
.section-title {
  color: #00ACED;
}
.archive-title {
  color: #00ACED;
}
.card__gen {
  border-top: none;
  background: #191919;
  color: #00FFFF;
}
.fa {
  color: #0FF;
  text-shadow: none;
}
.hover-content {
  background: #222;
  border-left: none;
  color: #00ACED;
}
.hover-content .title--hover {
  color: #00F;
}
.hover-content .gen--hover {
  color: #0FF;
  text-shadow: 0.5px 0.5px 0.5px rgba(0, 255, 255, 0.5);
}
.hover-content .misc--hover b {
  color: #0FF;
}
.link_tip::after {
  background: #222;
  color: #0FF;
}
nav {
  background: #1F1F1F;
  color: #00ACED;
  border-bottom: #000;
}
nav .nav__wrap {
  max-width: 1331px;
}
.nav__dropdown:hover ul {
  color: #00ACED;
}
nav input {
  color: #00ACED;
  background: transparent;
  padding: 5px 10px 10px;
  width: 187px;
}
nav footer {
  margin-right: -10px;
}
nav img {
  display: none;
  background: #1F1F1F;
  padding: 13.5px 10px;
}
.nav--active:before {
  background: #00ACED;
}
.nav__dropdown ul {
  background: #363636;
	border-bottom: none;
}
.nav__dropdown ul li {
  border-top: none;
}
.nav__dropdown i {
  color: #00FFFF;
}
nav .navSelect {
  margin-left: 7px;
  padding-top: 15.3px;
}
nav .navSelect span {
  color: #00ACED;
  text-align: left;
  margin-left: -40px;
}
nav .nav__wrap {
  margin-left: 0px;
}
nav .navSelect:nth-of-type(9) {
    margin-left: 85px;
}
}
