@-moz-document domain("forums.everybodyedits.com") {
/*:'######::'##::::'##:'##::: ##::'######::'##::::'##:'####:'##::: ##:'########:
/*'##... ##: ##:::: ##: ###:: ##:'##... ##: ##:::: ##:. ##:: ###:: ##: ##.....::
/* ##:::..:: ##:::: ##: ####: ##: ##:::..:: ##:::: ##:: ##:: ####: ##: ##:::::::
/*. ######:: ##:::: ##: ## ## ##:. ######:: #########:: ##:: ## ## ##: ######:::
/*:..... ##: ##:::: ##: ##. ####::..... ##: ##.... ##:: ##:: ##. ####: ##...::::
/*'##::: ##: ##:::: ##: ##:. ###:'##::: ##: ##:::: ##:: ##:: ##:. ###: ##:::::::
/*. ######::. #######:: ##::. ##:. ######:: ##:::: ##:'####: ##::. ##: ########:
/*:......::::.......:::..::::..:::......:::..:::::..::....::..::::..::........::
/* theme for forums.everybodyedits.com
/* made by zoey2070 */



/*****************************************************************
COLOUR SCHEME
*****************************************************************/

/* Background / Text
----------------------------------------------------------------*/
/* 715417 for text, c6a150 for dark fdfd96 for light af7b0a for links e9e46c as a medium//yellow*/
body {
	background: #c6a150;
	color: #715417
	}

.pun {
	color: #715417
	}

.pun .box, #adminconsole fieldset th {
	background-color: #FDFD96
	}

.pun td.tc2, .pun td.tc3, .pun td.tcmod, #postpreview, #viewprofile dd, .pun .forminfo,
#brdfooter #modcontrols, #adminconsole fieldset td, .pun .blockmenu .box, #adstats dd { /*moderate forum button thing*/
	background-color: #FDFD96
	}

.pun h2 { /*post rep & ad headeres & subforum divisions*/
	background-color: #e9e46c;
	color: #715417
	}

.pun th { /* ??? */
	background-color: #FDFD96 
	}

.pun legend {
	color: #715417
	}

.pun .blockmenu li.isactive a, #posterror li strong {
	color: #b4b4b4
	}

.pun .usercontent * {
	background: transparent;
	color: #b4b4b4
	}

.pun textarea, .pun input, .pun select { /*post reply & buttons */
	background-color: #e9e46c;
	color: #715417
	}

.pun .multiselect, .pun .checklist {
	color: #b4b4b4;
	}

.pun .checklist {
	border-color: #666;
	}

/* Posts
----------------------------------------------------------------*/

.pun .blockpost .box, .pun .postright, .pun .postfootright, .pun .deletemsg { /*post body*/
	background-color: #FDFD96
	}

.pun .postright, .pun .postfootright {
	border-left-color: #000 /*zz no idea what original value was lol or where this was even at*/
	}

.pun .postleft, .pun .postfootleft, .pun .blockpost label, .pun .codebox, .pun .quotebox { /*userinfo*/
	background-color: #e9e46c
	}

.pun .blockpost h2 { /*date/post#*/
	background-color: #e9e46c;
    color: #715417;
	}

.pun .blockpost h2 span.conr { /*969ea1 */
	color: #af7b0a
	}

.pun .postmsg ins, #punhelp samp ins { /*??? */
	background-color: #ff0;
	}

.pun hr { /* divides post/signature */
	background-color: #252525;
	color: #606060
	}


/* Borders
----------------------------------------------------------------*/

.pun .box { /* around edges of posts & subforum list*/
	border-color:#252525
	}

.pun td, #brdfooter #modcontrols { /* lines between subforum etc */
	border-color: #af7b0a
	}

.pun th { /*the lines of | topics  | posts |*/
	border-color: #af7b0a
	}

.pun fieldset { /*??*/
	border-color: #af7b0a
	}

#adminconsole td, #adminconsole th {
	border-color: #af7b0a
	}

.pun .quotebox, .pun .codebox, .pun .forminfo,
.pun .blockpost label, .pun .deletemsg { /*???*/
	border-color: #af7b0a
	}
    
    /* Links
----------------------------------------------------------------*/

.pun a:link, .pun a:visited {
	color: #af7b0a
	}

.pun a:hover, .pun a:active, .pun a:focus {
	color: #c9941f;
    text-decoration: none;
	}

.pun .postmsg .postimg a:link img, .pun .postmsg .postimg a:visited img {
	border-color: #20b6f6
	}

.pun .postmsg .postimg a:hover img, .pun .postmsg .postimg a:active img, .pun .postmsg .postimg a:focus img {
	border-color: #c9941f
	}

.pun h2 a:link, .pun h2 a:visited,
#brdmenu a:link, #brdmenu a:visited {
	color: #af7b0a
	}

.pun h2 a:hover, .pun h2 a:active,
#brdmenu a:hover, #brdmenu a:active {
	color: #af7b0a
	}

.pun .iclosed td.tcl a:link, .pun .iclosed td.tcl a:visited {
	color: #af7b0a
	}

.pun .iclosed td.tcl a:hover, .pun .iclosed td.tcl a:active {
	color: #af7b0a;
    text-decoration: none;
	}

.pun .maintenancelink a:link, .pun .maintenancelink a:visited {
	color: #af7b0a
	}

.pun .maintenancelink a:hover, .pun .maintenancelink a:active {
	color: #af7b0a;
    text-decoration: none;
	}

/* Status Indicators
----------------------------------------------------------------*/

.pun .icon {
	border-color: #e9e46c
	}

.pun .iredirect .icon {
	border-color: #22292F
	}

.pun .inew .icon {
	border-color: #af7b0a
	}
	
    
    /* Different55 Addons
----------------------------------------------------------------*/

a {
	text-decoration: none
}
.postmsg a, a:hover, a:active {
	border-bottom: 1px #715417 solid /*link underline*/
}

.crumbs a, h2 span {
	font-size: 1.1em
}

.pun .postfootright ul {
	border-top: #323232
}

div#brdheader .box {
	border: none
}

div#brdtitle {
	display: none
}

div#brdwelcome {
	margin-top: 2%; /*bottom box*/
	background-color: #e9e46c;
}

div#brdheader #brdmenu, div#brdheader div.box { /*CHANGES TOP BOX*/
	background-color: #e9e46c;
    border: 2px solid;
    border-radius: 5px 5px 0 0;
}

div#brdheader div#brdmenu ul {
	font: 1.25em Century Gothic, Arial, sans-serif;
	text-transform: uppercase
}

input, textarea, select, button {
	border: 1px solid #323232;
	background-clip: border-box
}

div#brdheader {
	margin-top: 50px;
}

.block #brdmenu {
	padding: 0px;
	margin-bottom: 50px;
	color: rgba(0,0,0,0);
}
#brdwelcome ul.conr {
	margin-top: 1.4em;
}

.separator {
	color: #b4b4b4;
}

.pun div h2, #brdstats, #brdfooter{ /* theme-dependent accents */
	border-top: 1px solid #715417
}

.postfoot .postfootright ul {
	border-top: 1px solid #323232
}

.pun .postfoot .postfootleft {
	border-right: 1px solid #323232;
	border-top: 1px solid #323232
}

.pun .postbody .postleft {
	margin-right: -1px;
	border-right: 1px solid #323232
}

.pun .postbody .postright {
	border-left: 1px solid #323232
}

td.tcl {
	padding: .55em .375em;
}

/* BBCode Addons */
.bgcolor {
	color: #1e1e1e;	
}

/* Warning warnings mod */
@keyframes name {
	from {background-color: #900}
	to {background-color: #111}
}

@-webkit-keyframes name {
	from {background-color: #900}
	to {background-color: #111}
}
}
