css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://(filelist\\.ro|flro\\.org)/(?!login.php).*') {
/* bg */
body{
 background: white;
}
html{
  background: white;
}
/* Navbar & buttons */
div.mainheader{
  background: black;
  height:70px;
}
#copyrightInnerRight > b:nth-child(1){
  display: none;
}
.subheader{
   width: 100%;
  height: 70px;
  margin:0px;
}
#navigation{
  margin:0px;
  top:0px;
  left: 70px;
  box-shadow: none;
  border: none;
  background: transparent;
}
#nav{
  position: absolute;
  top: 0px;
  margin: auto;
  padding:0px;
  height: 70px;
}
.active a{
  background-color: white;
  box-shadow: inset -1px 0px 0px black, inset 1px 0px 0px black;
  text-shadow: none !important;
}
#nav ul li{
  padding: 0px;
  margin: 0px;
}
#nav ul li a {
  color: white;
  height: 33px;
  padding-top: 25px;
  padding-left: 16px;
  vertical-align: middle;
}
#nav ul li a:hover{
  background-color: white;
  color: black;
  text-shadow: none;
  text-align: center;
}
.logo{
  background: url("http://s9.postimg.org/nod8c7eb3/FFLL.png") no-repeat;
  width: 70px;
  height: 70px;
  background-size: 70px;
  transition: .1s filter linear;
}
.logo:hover{
  filter: invert(100%);
}
.mainheader{
  height: 144px;
}
a{
  outline: 0px;
  transition: background-color .1s, color .1s;
}
.gototop{
  position: fixed;
  right: 0px;
  bottom: 0px;
  background-color: rgb(0, 0, 0);
  padding:5px;
  border-top-left-radius: 3px;
  box-shadow: 0px 0px 4px rgb(0, 0, 0);
  z-index: 1000;
}
/*statusbar*/
.statusbar{
  position: fixed;
  top:0px;
  right:0px;
  margin-top:0px;
  border-radius: 0px;
  z-index: 1000;
  background: black;
  border: 0px;
  height: 70px;
  padding: 0px;
}
.status_avatar{
  margin: 0px;
}
.status_avatar img{
  display: block;
    max-height:70px;
    width: auto;
    height: 70px;
}
.statusbar font{
  display: none;
}
.statusbar > div:nth-child(2) > div:nth-child(4){
  width:100px;
  position: absolute;
  top: 53px;
}
.statusbar > div:nth-child(2) > div:nth-child(5){
  width:150px;
  position: absolute;
  top: 53px;
  left: 110px;
}
.statusbar > div:nth-child(2) > div > span > img{
  filter: saturate(0);
}
.statusbar > div > div{
  margin-left: 5px;
}
/* Homepage*/
.nblock-headerleft{
  background: black;
}
.nblock-header{
  background: black;
}
.nblock-header h4{
  color: white;
  text-shadow: none;
}
.nblock-headerright{
  background: black;
  width: 7px;
}
.nblock-content{
  background: white;
  box-shadow: inset 1px 0px 0px black, inset -1px 0px 0px black, inset 0px -1px 0px black;
  padding-bottom: 20px;
}
.colhead{
  background: white;
  border: none;
}
.cblock .colhead img{
  border: none;
  filter: saturate(.5) brightness(50);
}
.nblock-bottom{
  display: none;
}
/*sblock*/
.sblock-headerleft{
  background: black;
}
.sblock-header{
  background: black;
}
.sblock-header h4{
  color: white;
  text-shadow: none;
}
.sblock-headerright{
  background: black;
}
.sblock-content{
  background: white;
}
.sblock-bottom{
  display: none;
}
.barmax, .bar{
  border-radius: 0px;
}
/*cblock*/
.cblock-top{
  display: none;
}
.cblock-headerleft{
  background: black;
}
.cblock-header{
  background: black;
}
.cblock-header h4{
  color: white;
  text-shadow: none;
}
.cblock-headerright{
  background: black;
}
.cblock-content{
  background: white;
}
.cblock-innercontent input, .cblock-innercontent select{
  border-radius: 0px;
}
input.btn{
  background: black;
  color: white !important;
  font-size: 9pt;
  padding-top: 1px;
}
input.btn:hover{
  background: black;
  color: white !important;
  font-size: 9pt;
  filter: invert(100%);
}
.cblock-bottom{
  display: none;
}
/*browse*/
.pager a, .pager a:link, .forumpager a, .forumpager a:link{
  border-radius: 0px;
  background: black;
  border: none;
  color: white;
}
.pager span.current, .forumpager span.current{
  border: none;
  border-radius: 0px;
}
.torrenttable{
  border-bottom: 1px solid black;
}
.torrenttable [title="Download"]{
  filter: brightness(100) drop-shadow(0px 0px .5px rgba(0,0,0,1)) drop-shadow(0px 0px .5px rgba(0,0,0,1)) drop-shadow(0px 0px .5px rgba(0,0,0,1));
}
/*Requests*/
td{
  border-color: black;
}
/* Profile */
.myprofile{
  border: 0px;
  background: black;
  color: white;
}
.myprofile a{
  color: white;
}
.cblock-innercontent > div:nth-child(2) > form:nth-child(1) > fieldset{
  padding-left: 0px !important;
  padding-right: 0px !important;
  border: 0px;
  background: white;
}
.cblock-innercontent > div:nth-child(2) > form:nth-child(1) > fieldset label{
  margin-left: 20px;
}
.cblock-innercontent > div > form > fieldset > legend{
  width: 100% !important;
  text-align: center;
  line-height: 25px;
  height: 25px;
  font-size: 15px;
  background: black;
}
.cblock-innercontent .colhead, .cblock-innercontent .heading{
  background: black;
  color: white;
  text-align: right;
  border: none;
}
/*Forum*/
#sectionmain .sectiontitle{
  background: black;
  color: white;
  border: none;
}
#forummain{
  border: none;
}
#forummain .forumrow{
  border: 1px solid black;
}
/*FLS*/
legend{
  background: black;
  color: white;
  width: 100%;
}
.cblock-innercontent fieldset{
  background: white;
  border: none;
  padding: 0px;
}
.separator{
  display: none;
}
/* Freeleech notice*/

center > [color="green"] {
  position: fixed;
  bottom: 0px;
  left: 0px;
  background: black;
  vertical-align: middle;
  padding: 15px 5px 15px 5px;
  z-index: 100;
  text-align: center;
  color: rgb(150, 255, 150);
}
/*footer*/
#footer{
  background: black;
}
#footerInner h2{
  color: white;
}
#footer ul{
  filter: brightness(1);
}
#footer a{
  color: white !important;
}
#copyrightInner{
  color: white;
}
}
