css:
@namespace url(http://www.w3.org/1999/xhtml);


/* Site wide tweaks */
@-moz-document url-prefix("http://community.battlefront.com") {
  
  /* Remove limit on content area */
  .ipsLayout_container {
    max-width: 20000px !important;
  }
  
  /* Square avatars */
  .ipsUserPhoto img, img.ipsUserPhoto, .ipsUserPhoto::after {
   border-radius: 0px !important;
  }
  
  /* Battlefront logo */
  #elLogo img {
    width: 200px !important;
  }
   /* Search bar*/
  #ipsLayout_mainNav li a {
    line-height: 26px !important;
  }
  #elSearchNavContainer #elSearch {
    margin: 0px 0px !important;
  }
  
  /* Bread crumbs */
  #ipsLayout_contentArea #elHeaderNavigation {
    padding: 5px 15px 5px 15px !important;
  }
  
  /* Reduce space around main area */
  #ipsLayout_mainArea {
    padding: 5px 15px !important;
  }
  
  /* Reduce announcmenets padding */
  div.cWidgetContainer ul li, div.cWidgetContainer ul li ul {
    padding: 0px 15px !important;
  }
  
  /* Reduce the sub forum entry padding */
  div.cForumList ol.ipsDataList li.ipsDataItem div, div.cForumList ol.ipsDataList li.ipsDataItem ul {
    padding: 5px 15px 0px 15px !important;
  }
  div.ipsDataItem_main h4.ipsDataItem_title.ipsType_large  {
    margin: 0px !important;
  }
  div.ipsDataItem_main div.ipsDataItem_meta {
    font-size: 12px !important;
    line-height: 1.0 !important;
  }
  ol.cForumList div.ipsDataItem_main div.ipsDataItem_meta, div.cForumList div.ipsDataItem_main div.ipsDataItem_meta {
    display: none !important;
  }
  
  /* Reduce space after each forum list */
  div.cForumList {
    margin-bottom: 5px !important;
  }
}

/* Forum specific tweaks */
@-moz-document url-prefix("http://community.battlefront.com/forum") {
  /* Reduce forum title padding */
  header.ipsSpacer_bottom {
    margin-bottom: 0px !important;
  }
  header.ipsSpacer_bottom div.ipsType_richText.ipsType_normal.ipsSpacer_top {
    display: none !important;
  }
  
  /* Reduce forum tool bar */
  ul.ipsToolList.ipsToolList_horizontal {
    margin-top: 5px !important;
    margin-bottom: 5px !important;
  }
  ul.ipsToolList.ipsToolList_horizontal a.ipsButton.ipsButton_medium {
    line-height: 28px !important;
  }
  
  /* Reduce title bar */
  div.ipsBox h2.ipsType_sectionTitle.ipsType_medium {
    padding: 4px 15px !important;
  }
  
  /* Reduce each topic line space */
  li.ipsDataItem div.ipsDataItem_icon  {
    padding: 5px 0px 5px 10px !important;
  }
  li.ipsDataItem div.ipsDataItem_main, li.ipsDataItem ul.ipsDataItem_stats {
    padding: 5px 10px !important;
  }
  li.ipsDataItem ul.ipsDataItem_lastPoster {
    padding: 0px !important;
  }
}

/* Topic specific tweaks */
@-moz-document url-prefix("http://community.battlefront.com/topic") {
  
  /* Hide announcements */
  #ipsLayout_mainArea div.cWidgetContainer {
    display: none !important;
  }
  
  /* Basic padding reduction for header area */
  #ipsLayout_mainArea {
    padding: 5px 15px !important;
  }
  ul.ipsToolList.ipsToolList_horizontal {
    margin: 4px 0px !important;
  }
  div.cTopic.ipsClear.ipsSpacer_top {
    margin-top: 2px !important;
  }
  h2.ipsType_sectionTitle.ipsType_reset.ipsType_medium {
    padding: 7px 15px !important;
  }
  
  /* Comment Content */
  /* Comment metadata */
  div.ipsComment_meta {
    padding: 10px 10px 5px 0px !important;
    font-size: 11px !important;
    line-height: 17px !important;
  }
  
  /* Comment controls */
  li.ipsJS_show, ul.ipsComment_controls {
    font-size: 11px !important;
    line-height: 17px !important;
  }
  
  /* Reputation controls */
  /* div.ipsLikeRep.ipsPos_right {
    display: none !important;
  } */

  /* Author Pane */
  /* Space around avatar */
  li.cAuthorPane_photo {
    margin: 0px 0px !important;
  }
  
  /* Space around author pane */
  article.cPost.ipsBox.ipsComment aside.ipsComment_author.cAuthorPane {
    padding: 5px 10px !important
  }
  
  /* Space around actual comment */
  div.cPost_contentWrap {
    padding: 0px 15px 5px 0px !important;
  }

  /* Fix bug: no space between Location and the author's location */
  aside.cAuthorPane li span.ft {
    padding-right: 5px !important;
  }
}
