  }
  .head-categories-title {
    background: #322D2D none repeat scroll 0% 0%;
    color: #FFF;
  }
  #search-header-button .btn-default {
    background: #2A2828 none repeat scroll 0% 0%;
  border-radius: 0px;}
  #btn-search {
    background: #2A2828 none repeat scroll 0% 0%;
    border-radius: 0px;}
.site-header.scrolled {
    box-shadow: 0px;
}
  .head-title {
    color: #B0B0B0;
    border-bottom: 1px solid #595454;
  }
  .list-grey li {
    border-bottom: 1px solid #3B3A3A;
  }
  .product-thumbnail .thumbnail-title {
    border-top: 1px solid #3B3A3A;
  }
  #landing-hot-review {
    border: 1px solid #3B3A3A;
  }
  #landing-hot-review .carousel-inner .item .bar0:nth-child(2n) {
