  }
  #fjb-home-categories, #forum-home-categories {
    border-bottom: 0px;
}
.head-categories-title {
    background: #3B3A3A none repeat scroll 0% 0%;
    border-bottom: 1px solid #3B3A3A;
}
  .head-title h2.title {
    border-left: 5px solid #9C9790;}
  footer.site-footer {
    background: #363434 none repeat scroll 0% 0%;
    border-top: 0px;
  }
  .filter-category {
    border-radius: 0px;
    background: #332F2F none repeat scroll 0% 0%;
  }
  .sidebar-category > li.listing-sidebar {
    background: #353232 none repeat scroll 0% 0%;
    color: #EFEFEF;
  }
  .product-thumbnail {
    background: #504C4C none repeat scroll 0% 0%;
  }
  .product-thumbnail .thumbnail-title {
    background: #544F4F none repeat scroll 0% 0%;
  }
  .carousel-category ul li {
    background-color: #383535;
  }
  .sidebar .sidebar-wrap > div, .sidebar .sidebar-wrap > section {
    background: #322F2F none repeat scroll 0% 0%;
  }
  .sidebar .regional ul li a {
    background: #383434 none repeat scroll 0% 0%;
    border: 0px;}
  
.top-products-homepage ul li a {
    background-color: #474545;
  border-radius: 0px;
}
.top-kaskuser .tab-content {
    background: #474343 none repeat scroll 0% 0%;
}
.top-kaskuser ul.nav-tabs > li.active a, .top-kaskuser ul.nav-tabs > li.active a:focus, .top-kaskuser ul.nav-tabs > li.active a:hover {
    color: #FFF;
  background: #302B2B none repeat scroll 0% 0%;
  border: 0px;
    
}
.top-kaskuser ul.nav-tabs > li > a {
    border: 0px;
   background-color: #3E3F3F;
}
.promoted-forum-homepage {
    background-color: #393535;
}
ul.tab-quick-update > li.active a, ul.tab-quick-update > li.active a:focus, ul.tab-quick-update > li.active a:hover {
    background-color: #201D1D;
  border:0px;
}
ul.tab-quick-update {
    background: #383232 none repeat scroll 0% 0%;
}
ul.tab-quick-update > li a {
  border-color: #3B3A3A transparent #3B3A3A #3B3A3A;}
ul.tab-quick-update > li:last-child a {
    border-right-color: none;
}
.quick-updates .tab-content {
    border:0px;
  background: #232222 none repeat scroll 0% 0%;}
.quick-updates .tab-content ul li {
  border-bottom: 1px solid #3B3A3A;}
.success-stories-item > div {
  background-color: #090909;}
.success-stories-item > div .success-stories-text {
    background: #3E3E3E none repeat scroll 0% 0%;
    border: 1px solid #3B3A3A;
}
  .kaskus-store h3 {
    background: #545352 none repeat scroll 0% 0%;}
  .kaskus-store .kaskus-store-form {
    background: #504B4B none repeat scroll 0% 0%;}
  .form-control {
    color: #555;
    background-color: #363232;
    border: 1px solid #3B3A3A;
    border-radius: 0px!important;}
  .btn-purple {
    color: #FFF !important;
    background: #0A0A0B none repeat scroll 0% 0%;
    border-radius: 0px;}
  footer.site-footer h3 {
    color: #F2EBEB;
    border-bottom: 1px solid #3B3A3A;}
