    background: #1B1D1B none repeat scroll 0% 0%;
}
  #fjb-search-result .table-header {
    background: #404241 none repeat scroll 0% 0%;
}
  #refine-search .categories-wrap {
    border: 1px solid #3B3A3A;
}
  #search-result .listing-table .listing th {
    background: #413D3D none repeat scroll 0% 0%;
}
  .post-entry .listing-table table td {
    border-bottom-color: #3B3A3A;
}
  .post-entry .listing-table table tbody th, .post-entry .listing-table table td {
    border-bottom: 1px solid #3B3A3A;}
  .table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
    border-top: 1px solid #3B3A3A;
}
  #search-result .listing-table .listing {
    margin-bottom: 15px;
    border-bottom: 1px solid #3B3A3A;
}
  .post-entry .listing-table table tbody {
    background: #3E3C3C none repeat scroll 0% 0%;
}
  .notice {
    background: #232221 none repeat scroll 0% 0%;
    border: 1px solid #3B3A3A;}
  body.fjb #breadcrumbs-wrap {
    border: 0px none;
    background: #2C2B2B none repeat scroll 0% 0%;}
  body.response.fjb .postlist .entry-head, body.response.fjb .postlist .kasad-h {
    background: #454746 none repeat scroll 0% 0%;}
.postlist {
    margin-bottom: 10px;
    background: #4B4B4B none repeat scroll 0% 0%;
  border: 1px solid #3B3A3A;}
.postlist .author {
    border-bottom: 1px solid #3B3A3A;
  border-top: 1px solid #3B3A3A;}
.product-detail-head {
  border-bottom: 1px solid #3B3A3A;}
.product-detail .nav-tabs li.active a {
    color: #6E6E6E;
    background: #2C2C2C none repeat scroll 0% 0%;
  border-color: #3B3A3A #3B3A3A #3B3A3A;}
.product-detail .nav-tabs {
  border-bottom: 1px solid #3B3A3A;}
.postlist .entry-footer {
    margin: 0px 10px;
  border-top: 1px solid #3B3A3A;}
.listing-wrapper .footer {
    border: 1px solid #3B3A3A;
  background: #454242 none repeat scroll 0% 0%;}
.thread-prevnext {
  background: #332E2E none repeat scroll 0% 0%;}
body.fjb .user-control-stick {
    background: #3C3A3A none repeat scroll 0% 0% !important;
  border-color: -moz-use-text-color #3B3A3A #3B3A3A;}
.site-header.scrolled {
    box-shadow: 0px 0px 0px #AAA;
}
.seller-info .seller-info-box {
    background: #353333 none repeat scroll 0% 0%;
  border: 1px solid #3B3A3A;}
.fjb-item {
    border-bottom: 1px solid #5C5555;
    background: #353333 none repeat scroll 0% 0%;
}
.fjb-item .fjb-item-image {
  border-right: 1px solid #5C5555;}
.fjb-item-list {
    border: 1px solid #5C5555;
}
#carousel-hot-review .clearfix .bar0:nth-child {
