  border-color: -moz-use-text-color #5C5555;}
.sidebar .momod-frame {
  border: 1px solid #5C5555;}
span.customSelect {
    background-color: #484646;
  color: #545454;}

body.forum .postlist .entry {
    color: #B4ABAB !important;
}
.user-control-stick {
    background: #141212 none repeat scroll 0% 0%;
  color: #F0E0E0;}
.quick-navigation{
background: #474141 none repeat scroll 0% 0%;
border-top: 1px solid #3B3A3A;
  border-bottom: 1px solid #3B3A3A;}
#breadcrumbs-wrap {
    background: #322F2F none repeat scroll 0% 0%;
  border: 1px solid #3B3A3A;}
#forum-thread-top {
    border: 1px solid #3B3A3A;
  background: #3E3D3D none repeat scroll 0% 0%;}
body.response.forum .postlist .entry-head, body.response.forum .postlist .kasad-h {
  background: #343638 none repeat scroll 0% 0%;}
.postlist .author {
    border-bottom: 1px solid #3B3A3A;
    border-top: 1px solid #3B3A3A;
    background: #504C4C none repeat scroll 0% 0%;
}
.forum-control {
  border-top: 1px solid #3B3A3A;
}
.postlist .entry-title {
  border-bottom: 1px solid #3B3A3A;
}
