@-moz-document url-prefix("https://forum.treeofsavior.com/") {
body, .headerWhiteBG, #reply-control, #reply-control .contents .wmd-input, #reply-control .contents .wmd-preview, .grippie, #reply-title, #search-term, .notifications li span, .read, #reply-control .wmd-button-bar, .select2-search, .select2-input, .select2-results, .category-combobox .topic-count, .select2-drop .topic-count, .category-combobox .category-desc, .select2-drop .category-desc, .select2-results .select2-highlighted, .select2-container a.select2-choice, .menu-panel.drop-down, .topic-list.categories a.title, .dropdown-menu span, .fa.tracking, .fa.watching, .user-main .about .secondary dd, .user-main .user-stream .item, .user-main .user-content, .wmd-button-row, .user-preferences .pagedown-editor, .user-preferences .pagedown-editor textarea, body .form-horizontal input, body .form-horizontal textarea, body .form-horizontal select, body .form-horizontal .input-prepend, body .form-horizontal .input-append, #reply-control .reply-area .ac-wrap, #private-message-users, .directory .filter-name, .dropdown-menu .disabled>a, .dropdown-menu .disabled>a:hover, .moderator .topic-body
	{
		background-color:black!important;
		color:gray!important;
	}
.nav-pills li a, .topic-list.categories .category h3 a[href], #topic-title h1 a, .names span a, th, .topic-list .num.posts a, th.posts.sortable.num, .topic-list .num, .topic-list .num.activity a, .topic-list a.title, #suggested-topics, .topic-list th:first-of-type, .topic-list td:first-of-type, #suggeste-topics th.category, .topic-list th:last-of-type, .topic-list td:last-of-type, .topic-list th, .topic-list td, #topic-footer-buttons p, .gutter .track-link, #reply-control #draft-status, #reply-control #file-uploading, .user-main .about .secondary dl dt, .user-main .user-stream .excerpt, .user-main .user-stream .time, .user-main .user-stream .delete-info, body .form-horizontal .control-label, .user-preferences .static, body label, table.badges-listing tr.title, body h1, body h2, .modal-header .close
	{
