	}
aside.quote .title, blockquote, .quote-controls, .quote-controls .back:before, .quote-controls .quote-other-topic:before, .topic-map h4, .topic-map .map .number, .topic-map .map i, .topic-map .buttons .btn, .autocomplete ul li a[href].selected, img.avatar
	{
		background-color:white!important;
		color:black!important;
	}
.topic-list a.title.visited:not(.badge-notification), .modal .modal-body label
	{
		color:gray!important;
	}
a.mention, span.highlighted
	{
		background-color:gray;
		color:black;
	}
.footerCompany, .footerCompany a, #reply-control .toggler, nav.post-controls .show-replies, nav.post-controls button, nav.post-controls .create i, .dropdown-menu .title, #topic-footer-buttons .btn, .open>.dropdown-menu, .btn-primary, .null, .user-main .about.collapsed-info .controls ul a, .user-main .about .details .primary .primary-textual, .user-main .about .details, .user-main .about .secondary dl, .nav-stacked>li>a, .user-main .about .controls .right, .user-badges-list .user-badge, .user-badges-list #user-card.show-badges .more-user-badges, #user-card.show-badges .user-badges-list .more-user-badges, div.ac-wrap div.item, table.badges-listing .user-badge, table.badges-listing #user-card.show-badges .more-user-badges, #user-card.show-badges table.badges-listing .more-user-badges, .modal-header, .modal-footer, .modal-body
	{
		background-color:black!important;
		color:white!important;
	}
.nav-stacked .active>a
	{
		background-color:#e45735!important;
	}
}

