css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('tapastic.com') {
	/*G1s: Home Page*/
	
	/*Section Title Font*/
	.section-row .inner .section-title-wrap .section-title {
		display: inline-block;
		line-height: 18px;
		margin-top: 7px;
		margin-bottom: 27px;
		font-family: "FunctionCapsMediumBold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		font-size: 48px;
		color: #FFF;
	}
	
	/*Comic Titles Font*/
	.series-list .series-item .title {
		display: block;
		text-overflow: ellipsis;
		white-space: nowrap;
		overflow: hidden;
		margin-bottom: 8px;
		line-height: 1.2;
		font-family: "function_pro_mediumbold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		font-size: 20px;
		color: #FFF;
	}
	/*Main Background*/
	
	#page-wrap {
		width: 100%;
		height: 100%;
		min-width: 748px;
		min-height: 100%;
		margin-bottom: 60px;
		background: #111 none repeat scroll 0% 0%;
	}
	
	/*Trending Section*/	
	.section-row.dark {
		background-color: #222;
	}
	
	/*G1e: Home Page =======================================================================================================*/
	
	
	/*G1s: Browse Comics Page*/
	/*Comic Titles Font*/
	
	.page-section .page-list-wrap .page-item-wrap .title {
		padding-top: 16px;
		font-family: "function_pro_mediumbold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		font-size: 20px;
		color: #FFF;
		text-overflow: ellipsis;
		white-space: nowrap;
		overflow: hidden;
	}
	/*Header Font*/
	
	.g-page-header .p-header {
		position: relative;
		font-family: "FunctionCapsMediumBold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		font-size: 48px;
		color: #FFF;
		margin-bottom: 40px;
		text-transform: lowercase;
		height: 40px;
		line-height: 26px;
		text-overflow: ellipsis;
		white-space: nowrap;
		overflow: hidden;
	}
	/*G1e: Browse Comics Page ==============================================================================================*/
	/*G1s: Read Comic Series Page*/
	/*Header Font*/
	
	#smart-view #main-streams #episodes-section .episodes-header .inner .episode-count {
		color: #FFF;
	}
	/*Header & Footer background, Episodes title font*/
	
	#smart-view #main-streams #episodes-section {
		transition: margin-left 0.3s cubic-bezier(0.2, 0.3, 0.25, 0.9) 0s;
		min-height: 500px;
		padding-top: 60px;
		margin-left: 260px;
		background: #FFDD00!important;
		color: #FFF;
	}
	/*Main Contents*/
	
	#smart-view #main-streams #episodes-section #episodes .episode {
		padding: 60px 0px 0px;
		background: #111 none repeat scroll 0% 0%;
	}
	/*Comic Divider Space*/
	
	#smart-view .ads {
		margin-top: 60px;
		background-color: #222;
		border-left: 1px solid #E2E2E2;
		width: 100%;
		box-sizing: border-box;
	}
	/*Left Side Bar*/
	
	#smart-view #left-side-bar {
		left: 0px;
		width: 260px;
		position: fixed;
		height: 100%;
		display: table;
		color: #FFF!important;
		background-color: #000;
		top: 0px;
		table-layout: fixed;
		z-index: 2;
		transition: width 0.3s cubic-bezier(0.2, 0.3, 0.25, 0.9) 0s;
	}
	
/*Total Number of Episodes Bar*/
#smart-view #left-side-bar .episode-list-bar .bar {
    padding-left: 10px;
    background-color: #333;
	}
	
	/*Episode List Font*/
	
	#smart-view #left-side-bar #episode-list .episode .episode-box .episode-info .title {
		padding-top: 5px;
		line-height: 20px;
		text-overflow: ellipsis;
		white-space: nowrap;
		overflow: hidden;
		font-size: 16px;
		font-family: "function_pro_mediumbold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		color: #FFF;
	}
	

	/*Episode hovering*/
	#smart-view #left-side-bar #episode-list .episode:hover {
    background-color: #222 !important;
    opacity: 1;
	}
	
		/*Currently Selected Episode*/
	#smart-view #left-side-bar #episode-list .episode.on {
    background-color: #333 !important;
    opacity: 1;
	}

	
	/*G1e: Read Comic Series Page ==============================================================================================*/
	
	
	/*G1s: User Page*/
	.page-shadow {
    background-color: #111;
	}
	#profile-page .main > .inner {
		line-height: 0;
		padding: 70px 0px 0px 40px;
		overflow: hidden;
		height: 100%;
		width: 100%;
		min-width: 720px;
		box-sizing: border-box;
		display: table;
		table-layout: fixed;
		position: relative;
		background: #111;
	}
	/*Comment Box*/
	
	.comment-section.wall .write-box .input-wrap {
		width: 100%;
		background: #222;
	}
	.comment-section .write-box .input-wrap .comment-body {
		font-family: "function_pro_bookregular", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		border: 0px none;
		box-shadow: none;
		resize: none;
		font-size: 18px;
		line-height: 22px;
		padding: 12px 0px;
		overflow: hidden;
		height: 22px;
		min-height: 22px;
		width: 100%;
		background: #222;
		color: #FFF;
	}
	/*Comments User Names Font*/
	
	.comment-section .comment-streams .body-wrap .body-header .commenter {
		font-family: "function_pro_mediumbold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		font-size: 14px;
		color: #FFF;
	}
	/*Comic, Series Title Font*/
	
	.aside-wrap .aside-list .series-info .title {
		font-family: "function_pro_mediumbold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		font-size: 20px;
		color: #FFF;
		text-overflow: ellipsis;
		white-space: nowrap;
		overflow: hidden;
		padding-top: 2px;
	}
	/* Left Side Bar (User Info)*/
	
	#profile-page .user-info-section .scrollbar-wrap .viewport {
		height: 100%;
		background: #111;
	}
	#profile-page .user-info-section .inner {
		width: 230px;
		padding: 70px 20px 0px 30px;
		background: #111;
	}
	/*User Name Font*/
	
	#profile-page .user-info-section .inner .user-desc .author {
		font-family: "function_pro_mediumregular", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		font-size: 28px;
		color: #FFDD00;
		margin-bottom: 16px;
		line-height: 1.2;
	}
	/*G1e: User Page ==============================================================================================*/
	
	
	/*G1s: Pulish*/
	.creator-row.dark-gray {
		background-color: #222;
	}
	.creator-row .inner.two-c-row .row-narrow .desc.main-title {
		font-family: "FunctionCapsMediumBold", "Helvetica Neue", Helvetica, sans-serif;
		font-weight: normal !important;
		font-style: normal !important;
		margin-top: 30px;
		color: #FFF;
		font-size: 42px;
	}
	/*G1e: Publish ==============================================================================================*/
	
	
	/*G1s: Forums ==============================================================================================*/
	
	/*G2s: Thread +++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/
	/*Main*/
	html {
		color: #FFF;
		font: 14px/20px "function_pro_bookregular", "Helvetica Neue", Helvetica, Arial, sans-serif;
		background: #111;
	}
	
	/*Thread Title*/
	#topic-title h1 a {
 		color: #FFF;
	}
	
	/*--- Quote Block ---*/
	/*Title*/
	aside.quote .title {
		border-left: 5px solid #CFCFCF;
		background-color: #333;
		color: #FFF;
		padding: 12px 12px 1px;
	}
	/*Quote Content*/
	blockquote {
		background-color: #222;
		border-left: 5px solid #CFCFCF;
		overflow: hidden;
	}


	
	
	/*G2e: Thread +++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/
	
	/*G1e: Forums ==============================================================================================*/

} /*end domain*/
