css:
@-moz-document domain("8tracks.com") {
/* black bgs */
#thin_header, #header_content, #explore_button, .search, .autocomplete, .result.tags, #sort_select, .turquoise_button, #logged_in_dropdown, .my_collection:hover, .fav_track, .reason, .avatar-icon, #browse, .tags_editor, #mix_edit_details, .publish_options, .red_button, .gray_button, .mix_status, #track_selection_header, .even, #track_search_header, .crate_header, .editable_track, #stats_sharing, .upload_placeholder, .inverted_counter, #related_artists, .title_tag
{background: black !important;}

body, .card, #new_collection_name, .item, #topnav_element, #play_area, #mix_edit_description, #mix_edit_name, .roundText
{background-color: black !important;}

/* black bg - tags, comment box, add to collections popup */
.tag, .new_review, .mediumText, .content
{background: black !important;}

/* white fonts */
.autocomplete, .token-input, .track, #description_html, .detail, textarea, h1, h2, #bio_container, .content, label, #mix_edit_name, .cover_art_instagram, .active, .mentions_highlights /*!*/, .inverted_counter, .notifications_counter, .q
{color: white !important;}

/* gray fonts */
p, .track_metadata, .by_artist, .unplayable_warning, .track:before, .timestamp, body 
{color: #7c7c7c !important;}

.token-label, .token, .notifications_counter
{background: #221f1f !important;}

/* black fonts */
#mix_name, .mix-data
{color: black !important;}

/* link colors */
a, .turquoise.right
{color: #999999 !important;}
a:hover, .my_collection:hover, .feed-filter.active, .result:hover, .inverted_counter:hover
{color: #53bab3 !important; background: transparent !important;}

/* dark gray bgs - collection header, profile menu tab */
.background-blur-container 
{background: #1a1a1a !important;}

/* fav color */
.fav.active 
{color: #1fe1d4 !important;}

/* follow button border color */
a.follow 
{border-color: white;}

/* menu item hover bg color */
a.menu-item:hover
{background: black !important;}

/* border around add to collections popup */
.content, .turquoise_button
{border: 2px solid white !important;}

/* track number font size */
.track:before 
{font-size: 20px !important;}

/* colors for home page tabs */
#loadingbar {background: gray;}
a.selected {border-color: white !important;}
.big_menu:after {border-bottom: none;}
a.selected:after {border-bottom: none !important;}
.item.selected {color: white !important;}

/* transparent bg */
a.close 
{background-color: transparent !important;}

/* mix status colors */
.amber
{box-shadow: none !important; background-image: none !important; background: #53bab3 !important;}

/* remove border */
.content {border: none !important;}

/* scrollbar */
::-webkit-scrollbar-thumb:vertical { 
background-color: #999999;
height:auto;
}
::-webkit-scrollbar-thumb:horizontal { 
background-color: #999999;
height:7px;
}
::-webkit-scrollbar { 
height:7px;
width:7px; 
background-color: black;
}
}
