/*Screwball - Orange */

@-moz-document domain("friendsinyourhead.com")


{
.brd .gen-content, .brd .main-content {
    background: #00234b;
    border-color: #111;
    border-width: 0px 0px 0px;
}
.brd .main-frm .ct-group, .brd #brd-announcement {
    background: #1d1d1d;
    border-color: #111;
    border-width: 0px 0px 0px;
}
  .brd .main-head .hn, .brd .main-foot .hn {
     background: #00234b ;
}
.brd .main-head, .brd .main-foot {
    background: #00234b;
    border-style: solid;
    border-color: #111;
    border-width: 0px 0px 0px;
}
.brd .item-summary {
    background: #004798;
    border-style: solid;
    border-color: #111;
    border-width: 0px 0px 0px;
   
}
 
.brd .main-content .main-item {
    border-top-style: solid;
