    position: relative;
    line-height: 1em;
}
 .brd .post {
    border-style: solid;
    border-width: 0px;
     background: #004798;
}
    
    .brd .postfoot {
    padding-left: 18em;
    border-top-style: solid;
     border-color: #FFF;   
    }
  .brd .posthead .hn {
  
    border-style: solid;
    border-width: 0px 0px 0px;
}  
    
}
