css:
@-moz-document domain("itstoohard.com") {

/*ITHD 1.0*/


/*body and header*/
body {
  background: radial-gradient(#060709,#1C1F28);
  color: #DDE7EE;
}

.page {
  background: rgba(220,220,255,.06);
  border: 0;
  border-radius: 10px;
}

.headerLogo {
  background: radial-gradient(rgba(220,220,255,.4),rgba(220,220,255,.05));
  border-radius: 10px 10px 0 0;
  margin-bottom: 10px;
  border-bottom: 5px solid #96A2FF;
}

.headerBottom {
  border-top: 0;
  border-bottom: 0;
  margin: 0 10px;
}

a.nav:link, 
a.nav:visited {
  font-weight: 500;
  color: #11131A;
  background: #D0E0FF;
  padding: 5px 15px;
  border-radius: 4px;
  transition: .2s;
  min-width: 0;
}

a.nav:hover {
  color: #EBECF5;
  background: #4466B5;
}



/*stats*/
div.sideBlock_Stats {
  background: linear-gradient(#82ADFF,#D0E0FF);
  border: 0;
  border-radius: 5px;
}

div.sideBlockHeader_Stats {
  padding-top: 5px;
  background: none;
  border: 0;
}



/*leaderboards*/
div.sideBlock_Leaderboard {
  background: linear-gradient(#FFFF96,#FFFFD0);
  border: 0;
  border-radius: 5px;  
}

div.sideBlockHeader_Leaderboard {
  padding-top: 5px;
  background: none;
  border: 0;
}



/*instructions*/
div.sideBlock_Instructions {
  background: none;
  border: 0;
}

.sideBlock_Instructions div.sideBlockHeader_Instructions {
  padding-top: 5px;
  background: #96FFA2;
  border: 0;
  border-radius: 5px 5px 0 0;
}

.sideBlock_Instructions div.sideBlockContent {
  margin: 0 0 10px 0;
  background: linear-gradient(#96FFA2,#D0FFE0);
  padding: 10px 15px;
  border-radius: 0 0 5px 5px;
}



/*main*/
div.blockHeader, 
div.userBlockHeader {
  background: linear-gradient(#D0E0FF,#C6DAFF);
  border: 0;
  color: #243059;
  padding: 10px;
  border-radius: 5px;
}

div.blockMain {
  border-right: 0;
}

div.blockContent {
  margin: 20px 0;
}

div.subBlock {
  border: 0;
  margin: 20px 0px;
}

div.subBlockHeader {
  background: #35456F;
  border-bottom: 0;
  color: #DBE0EE;
  padding: 5px 10px;
  border-radius: 5px 5px 0 0;
}

div.subBlockMain {
  background: linear-gradient(#293143,#222837);
  color: #E6E9F0;
  padding: 10px 10px 5px 10px;
  border-radius: 0 0 5px 5px;
}

.subBlockContent input {
  padding: 5px;
  background: rgba(220,240,255,.9);
  border: 0;
  border-radius: 3px;
  font-size: 12px;
}

textarea.textEntry {
  padding: 5px;
  background: rgba(220,240,255,.9) !important;
  border: 0;
  border-radius: 3px;
  font-size: 12px;
}

#Div_Question .subBlockContent input {
  border-radius: 3px 0 0 3px;
}

input[type="button"], 
input[type="submit"], 
input[type="reset"], 
input[type="file"]::-webkit-file-upload-button, 
button {
  cursor: pointer;
  color: #2D315B;
  padding: 5px 10px;
  background: rgba(220,240,255,.9);
  border: 0;
  border-radius: 3px;
  margin-left: -3px;
  font-size: 12px;
  transition: .2s;
}

input[type="button"]:hover, 
input[type="submit"]:hover, 
input[type="reset"]:hover, 
input[type="file"]::-webkit-file-upload-button:hover, 
button:hover {
  color: #EBECF5;
  background: #4466B5;
}

#Div_Question input[type="submit"] {
  border-radius: 0 3px 3px 0;
}


/*footer*/
div.blockFooter {
  background: none;
  border-top: 0;
  border-bottom: 0;
  color: #DDE7EE;
  padding: 10px;
}

.footer {
  color: #DBE0EE;
  padding: 7px 20px 10px 20px;
  background: #35456F;
  border-top: 5px solid #96A2FF;
  font-size: 11px;
  height: auto;
  border-radius: 0 0 10px 10px;
}

.footer a:link, 
a:visited, 
.subBlockHeader a:link, 
.subBlockHeader a:visited, 
a.navBlack:link, 
a.navBlack:visited, 
a.navBlack:hover, 
a.navBlack:active {
  color: #DBE0EE;
  text-decoration: none;
  transition: .2s;
}

.footer a:hover, 
.subBlockHeader a:hover, 
a.navBlack:hover {
  color: #BAD2EF;
  text-decoration: none;
}



/*user*/
a.navUser:link, 
a.navUser:visited {
  min-width: 0;
  color: #161F27;
  background: #96A2FF;
  padding: 5px 20px;
  margin-right: 10px;
  border-radius: 4px;
  transition: .2s;
}

a.navUser:hover {
  color: #E8EBFF;
  background: #6477FF;
}

#M_LinkButton_Add {
  font-weight: 500;
  color: #232938;
  background: #C6DAFF;
  padding: 5px 10px;
  border-radius: 4px;
  transition: .2s;
  font-size: 12px;
  text-decoration: none;
}

#M_LinkButton_Add:hover {
  color: #E6EEFF;
  background: #4880FF;
}

#M_Repeater_Questions_LinkButton_Remove_0, 
#M_Repeater_Questions_LinkButton_Remove_1, 
#M_Repeater_Questions_LinkButton_Remove_2, 
#M_Repeater_Questions_LinkButton_Remove_3, 
#M_Repeater_Questions_LinkButton_Remove_4, 
#M_Repeater_Questions_LinkButton_Remove_5, 
#M_Repeater_Questions_LinkButton_Remove_6, 
#M_Repeater_Questions_LinkButton_Remove_7, 
#M_Repeater_Questions_LinkButton_Remove_8, 
#M_Repeater_Questions_LinkButton_Remove_9, 
#M_Repeater_Questions_LinkButton_Remove_10, 
#M_Repeater_Questions_LinkButton_Remove_11, 
#M_Repeater_Questions_LinkButton_Remove_12, 
#M_Repeater_Questions_LinkButton_Remove_13, 
#M_Repeater_Questions_LinkButton_Remove_14, 
#M_Repeater_Questions_LinkButton_Remove_15 {
  font-weight: 500;
  color: #B6C1DC;
  transition: .2s;
  font-size: 12px;
  text-decoration: none;
}

#M_Repeater_Questions_LinkButton_Remove_0:hover, 
#M_Repeater_Questions_LinkButton_Remove_1:hover, 
#M_Repeater_Questions_LinkButton_Remove_2:hover, 
#M_Repeater_Questions_LinkButton_Remove_3:hover, 
#M_Repeater_Questions_LinkButton_Remove_4:hover, 
#M_Repeater_Questions_LinkButton_Remove_5:hover, 
#M_Repeater_Questions_LinkButton_Remove_6:hover, 
#M_Repeater_Questions_LinkButton_Remove_7:hover, 
#M_Repeater_Questions_LinkButton_Remove_8:hover, 
#M_Repeater_Questions_LinkButton_Remove_9:hover, 
#M_Repeater_Questions_LinkButton_Remove_10:hover, 
#M_Repeater_Questions_LinkButton_Remove_11:hover, 
#M_Repeater_Questions_LinkButton_Remove_12:hover, 
#M_Repeater_Questions_LinkButton_Remove_13:hover, 
#M_Repeater_Questions_LinkButton_Remove_14:hover, 
#M_Repeater_Questions_LinkButton_Remove_15:hover {
  color: #DA6565;
}

#M_Button_Save {
  font-weight: 600;
  color: #DFE9FF;
  background: #4880FF;
  padding: 10px 20px;
  border-radius: 4px;
  transition: .2s;
  min-width: 0;
  font-size: 14px;
  border: 0;
  cursor: pointer;
}

#M_Button_Save:hover {
  box-shadow: 0 0 20px 0 #759EFF;
}

.trailer {
  color: #D5E3FF;
  width: auto;
}


}
