css:
@-moz-document url-prefix("https://www.assembla.com/") {
*{
    outline: 0;
}
input::-moz-focus-inner { 
  border: 0; 
}
.navigation, .navigation.portfolio-navigation{
    background: #455464;
    box-shadow: rgba(0, 0, 0, .45) 0 0 10px;
}

.input--s .main-search .mentionable .open{
    background: green !important;
}

.gray-btn, .gray-btn-small, .gray-btn-big, input.gray-btn, input.gray-btn-small, input.gray-btn-big{
    font-weight: lighter !important;
    background: #455464;
    color: white !important;
}

.s-table-header{
    background: #455464;
    color: white;
}

a, a:link, a:visited{
 color: rgba(0, 0, 0, .87);   
}

ul.menu-submenu li a{
 color: #E91E63;
}

.s-table-header{
	padding: 0.5em;
}

.s-table-header h3 a{
    color: white;
}

.listing thead tr th{
    background: none;
}

.tickets tbody tr td{
    padding: .25em !important;
}

.text-icon input, a{
    font-family: Helvetica-Neue, sans-serif;
}

div.new-context-menu{
    box-shadow: rgba(0, 0, 0, .25) 0 0 10px;
}

div.new-context-menu, div.new-context-menu li ul{
    border-radius: 5px;
    border: none;
}

.blocklist li:hover{
    border-radius: 5px;
    border: none;
    background: green;
}

.s-box-comment .s-self-header{
    background: #455464 !important;
    color: white;
}

.activity .ticket-comments .s-box-comment .s-self-header .changes-description em{
    color: white !important;
}

.s-box-comment .s-self-header *{
    color: white;
}

.s-box-comment .s-header{
    background: #E1F5FE;
}

.navigation .nav-search form, .navigation .nav-search .main-search{
    background: none !important;
}
}
