css:
@-moz-document domain("neogaf.com") {

#toolbar {
  /* display: none; */
  position: absolute;
  top: 30px;
  left: 345px;
  right: 0;
}

#usercptools_menu {
  left: auto !important;
  right: 40px;
}

@media screen and (max-width: 1230px) {
  #headeruser {
    float: none;
  	clear: left;
  }
  .headerusersep {
  	display: none !important;
  }
  #usercptools {
  	margin: 0 !important;
  }
}
/*
#posts {
  	box-shadow:0 0 5px #888;
}
*/
.post img {
	/* box-shadow:0 0 3px #888;
	background:#eee !important; */
  	border-radius: 3px;
  	margin-bottom: 15px;
    /* width: 100%;
    height: auto; */
}

.post img ~ br + br, a[href$=".jpg"] ~ br + br, a[href$=".png"] ~ br + br, a[href$=".gif"] ~ br + br {
  display:none;
}

@media screen and (max-width:1058px) {
  #logo {
    width: 280px;
  }
  #main .wrap {
    width: 100%;
    padding: 0 !important;
    margin: 0;
    overflow: hidden;
  }
  .large-button.submit, .pagenav {
    margin: 12px;
  }

}

  .postbit {
  	padding-left: 0;
  }
  .postbit-details {
    float: none;
    width: 100%;
  	min-height: 100px;
    box-sizing: border-box;
    margin-left: 0;
  	padding: 15px 15px 15px 100px;
  	border: 0;
    border-bottom: 1px solid #d0d0d0;
  }
  .postbit-details-usertitle {
  	max-width: 120px;
  }
  .postbit-details-avatar {
  	position: absolute;
  	top: 15px;
  	left: 15px; /* 165px; */
  	margin: 0;
  	width: 70px;
  	overflow: hidden;
  }
  .postbit-details-avatar img {
  	width: auto;
    max-height: 70px;
	/* margin-top: -25%; */
  }
  .postbit-details :nth-child(4) {
  	float: right;
    padding: 0 !important;
    margin-top: -20px;
  }
  .postbit-post {
    float: none;
    border: 0;
  	padding: 15px;
  }
  .post-meta {
    border: 0;
    position: absolute;
    top: 15px;
    left: 15px;
    right: 15px;
    text-align: center;
  	font-size: 12px;
  }
    
}
