css:
@-moz-document domain("player.spotify.com") {
/* Make top panel higher for player controls to fit */
header#header {
    height: 85px;
}

/* Player controls set to top & center */
#view-player .player-controls-container {
    position: fixed;
    top: 33px;
    left: 220px;
    width: 60%;
}

/* Profile Icons smaller width */
div#view-profile-menu {
    width: 30%;
}

/* Now Playing to top on the left */
#view-now-playing .caption {
    position: fixed;
    top: 33px;
}

/* Move Search textbox to the left, above player controls */
html.has-native-navbar #view-search {
    margin-left: 250px;
}

/* Upgrade button */    
div#upgrade-product {
    display: none;
}

/* Hide unneeded footer controls container */
footer#view-player {
	height: 0;
}

/* Nyan Cat Progress bar */

/* Handle */
.progress-bar .handle, .progress-bar-thick .handle {
   background: url("data:image/gif;base64,") !important;
	
    display: block;
    width: 34px !important;
    height: 21px;
    opacity: 0;
    position: absolute;
    right: -15px;
    top: -6px;
    border: 0;
}

/* Progress bar rainbow background */
.progress-bar .inner, .progress-bar-thick .inner { 
    background: url("data:image/png;base64,") repeat-x !important; 
    bottom: 0;
    background-size: 1px 4px !important;
    left: 0;
    position: absolute;
    right: auto;
}

/* Progress bar rainbow background bigger on hover */
#progressbar:hover .inner, .progress-bar-thick .inner {
	background-size: 1px 6px !important;
}
#volumebar:hover .inner, .progress-bar-thick .inner {
	background-size: 1px 6px !important;
}

/* Search Suggest Dropdown */
iframe#app-suggest {
position: fixed;
left: 265px;
}
}
