css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("38.103.161.155") {
  body, #wrapper,form {background:rgb(16,18,20); border:none;}
  body {padding-top:30px;}
  #header, #ad_headerbanner,#ad_text,.portalbox {display:none;}
  #wrapper #menu {display: none;}
  body > #menu {position:fixed;top:0px;left:0px;z-index:10000;width:100%;}
  #menu {background:rgb(39,45,53);border:none;margin-bottom:1px;}
  #menu ul {border:none;padding:0px;}
  #menu ul li.hover, #menu ul li.current {background:rgb(39,45,53);border:none;padding:0px;margin:0px;}
  #menu ul li.hover a, #menu ul li.current a {padding:1px 12px;}
  #menu ul li a {font-size:11px;background:rgb(31,36,42);border:1px solid rgb(19,21,25);border-radius:3px;color:rgb(152,171,185);padding:1px 12px;margin:3px;}
  #menu ul li a:hover {background:rgb(27,31,36);}
  .popupmenu_popup.headermenu_popup,#threadtypesmenu_menu {background:rgb(39,45,53);border:1px solid rgb(19,21,25);}
  .popupmenu_popup.headermenu_popup li, #threadtypesmenu_menu li {border-bottom:1px solid rgb(19,21,25);}
  .popupmenu_popup.headermenu_popup li a, #threadtypesmenu_menu li a {color:rgb(152,171,185);padding:1px 12px;margin:3px;}
  
  #foruminfo {display:none;}
  .pages_btns {background:rgb(39,45,53);position:fixed;bottom:0px;left:0px;z-index:10000;}
  .pages, .threadflow {border:none;padding:0px;}
  .pages a, .pages a.next, .pages strong,.pages a:hover, .threadflow a, .threadflow a:hover {line-height:16px;font-size:11px;background:rgb(31,36,42);border:1px solid rgb(19,21,25);border-radius:3px;color:rgb(152,171,185);padding:1px 8px;margin:3px 1px;}
  .pages a:hover,.threadflow a {background:rgb(27,31,36);text-decoration:none;}
  .pages kbd {border:none;}
  .pages kbd input {padding:2px 12px;background:rgb(16,18,20);border-radius:10px;border-width:0px;color:rgb(152,171,185);}
  
  #newspecial,.replybtn {display:none;}
  .mainbox, .threadlist {background:rgb(16,18,20);border:none;}
  #headfilter {display:none;}
  .mainbox.threadlist > h1 {background:rgb(16,18,20);border:none;}
  .headactions > a, .headactions > span {text-decoration:none;font-size:11px;background:transparent !important;border:none;border-radius:3px;color:rgb(152,171,185) !important;padding:1px;margin:3px;}
  .mainbox thead.category td,.mainbox thead.category th,.mainbox thead td,.mainbox thead th  {background:rgb(39,45,53);color:rgb(152,171,185);}
  .mainbox thead.category td a {color:rgb(152,171,185);}
  
  .forumlist table, .threadlist table {border:none;}
  .forumlist, .threadlist {background: transparent;}
  
  .forumlist tbody th, .forumlist tbody td, .threadlist tbody th, .threadlist tbody td  {background: transparent;border:none;border-top:1px solid rgb(16,18,20);color:rgb(152,171,185);}
  .forumlist tbody tr:hover td, .forumlist tbody tr:hover td, .threadlist tbody tr:hover td, .threadlist tbody tr:hover td,.forumlist tbody tr:hover th, .forumlist tbody tr:hover th, .threadlist tbody tr:hover th, .threadlist tbody tr:hover th  {background:rgb(16,18,20) !important;border-top:1px solid rgb(39,45,53);}
  #footfilter, .legend,#footercontainer,div[id*="ad_footerbanner"],.maintable {display: none;}
  .viewthread ins, .mainbox ins {border:none;}
  .mainbox h1, .mainbox h3, .mainbox h6 {background:rgb(16,18,20);border:none;color:rgb(152,171,185); }
   .postinfo, .postactions {display:none;}
  
  .postauthor > dl, .postauthor > ul {display:none;}
  .viewthread td.postcontent, .viewthread td.postauthor {background:transparent;color:rgb(152,171,185);}
  .viewthread table, .viewthread table {border-collapse: collapse;} 
  .postauthor cite {border:none;}
  fieldset {display:none;}
  
  .postattachlist h4, .tradethumblist h4, .pollpanel h4, .activitythread h4, .typeoption h4 {border:none;background:transparent;}
  .postattachlist {background:rgb(31,36,42);}
  .t_attachlist {border:none;}
  .t_attachlist a {color:rgb(152,171,185);}
  .postauthor cite a,.postauthor p em {border:none;color:rgb(152,171,185);}
}
