css:
@-moz-document domain("boardgamegeek.com"), domain("rpggeek.com"), domain("videogamegeek.com") {
html,
body,
.geekmodule tr:nth-child(even),
.innermoduletable tr:nth-child(even) {
    background-color: #121212;
}

.geekmodule tr:nth-child(odd),
.innermoduletable tr:nth-child(odd),
.menu_quickbar,
.mainmenutabs,
.menu_login,
.whitebox,
.bluebox,
.forum_table td,
.forum_index_table td,
.comment_toolbar,
div.article,
div.header,
div.recommend_block,
.profile_block {
    background-color: #232323;
}

html,
body,
.mainmenutabs li a,
#quickbar_label,
.profile_block {
    color: white;
}

.whitebox,
.bluebox {
    color: #BBBBBB;
}

a:link,
a:visited {
    color: #5275F1;
}

.forumlink a:visited,
.forumlink a:link,
.forumlink a:hover,
.forumlink a:active,
.objectlink a:visited,
.objectlink a:link,
.objectlink a:hover,
.objectlink a:active,
.profile_title
{
    color: #7B7B7B;
}

.quote {
    background-color: #404040;
}
}
