css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zismo.biz") {
  #branding {
    height: 83px;
    background: #3598dc;
    border-bottom: #4faded solid 4px;
  }
  #primary_nav {
    background: #e6f1f7;
    height: 42px;
    line-height: 42px;
    border: 0;
  }
  #community_app_menu {
    height: 42px;
    margin: 0;
  }
  #community_app_menu > li {
    border: 0;
  }
  #community_app_menu > li > a {
    padding: 0 21px;
    font-size: 14px;
    color: #6f8b96;
    font-weight: bold;
  }
  #community_app_menu > li > a span {
    color: inherit !important;
  }
  #community_app_menu > li > a:hover {
    background: transparent;
    color: #4faded;
  }
  #community_app_menu > li > a b {
    font-weight: inherit;
  }
  #quickNavLaunch {
    display: none !important;
  }
  #logo {
    background: url(http://i.imgur.com/l5962ls.png) no-repeat;
    overflow: hidden;
    margin: 20px 31px 0 24px;
    width: 162px;
    height: 44px;
  }
  #logo a {
    display: block;
    text-indent: 200% !important;
  }
  #user_navigation.logged_in {
    padding: 28px 0 0 0;
  }
  #user_navigation.logged_in li {
    background: transparent;
    box-shadow: none;
    height: 29px;
    padding: 0;
    line-height: 29px;
  }
  #user_navigation.logged_in li a {
    color: #fff;
    text-shadow: none;
    font-size: 14px;
    padding: 0;
  }
  #user_link_dd {
    display: none;
  }
  #notify_link,
  #inbox_link,
  #logout_link {
    border: #9acced solid 1px;
    border-radius: 5px;
    width: 27px;
    height: 27px;
    background: url(http://i.imgur.com/BOqdCBD.png) no-repeat 0 50%;
    padding: 0;
    display: block;
  }
  #user_link {
    margin-right: 17px;
  }
  #inbox_link {
    background-position: 4px center;
  }
  #notify_link {
    background-position: -32px center;
  }
  #logout_link {
    background-position: -66px center;
  }
}
