@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("startpage.com"), domain("ixquick.com") {

input#query {background:#ffffff!important}
input#query_top, input#query_bottom {background:#ffffff!important; height: 38px; font: 16px arial,sans-serif}

ul#video-lemmon-slider-ul {background: #f7f7f7 !important;}
ul#pics-lemmon-slider-ul {background: #f7f7f7 !important;}
div#video-full-display.video {background: #222222 !important;}
div#pics-full-display.pics {background: #222222 !important;}
label#full-video-name.font_bold {color: #d6d6ca !important;}
label#full-image-name.image-info {color: #d6d6ca !important;}
tbody {color: #7d7d7d !important;}
div#wrapper.media_wrapper {background: #ffffff!important;}
 
div.selected-img-info {color: #7d7d7d !important;}
a#full-image-view {color: #7d7d7d !important;}
a#full-image-page {color: #7d7d7d !important;}
a#full-image-page-anonymous {color: #7d7d7d !important;}
.t_img {display:none;}
  
body.air-theme .navbar-default.navbar-static-side.unselectable {top:50px!important;}

body.air-theme {margin-top: 0px;}

/*ixnay*/
.sponsored, .head2, .midline_right, .enhanced_by_td, .cloud, #plus-icon, #home-icon, #results .ix-icons, #sponsored_csa1, #sponsored_csa2 {display: none;}
  
/**Top Bar**/
.pics_results, .video_results {margin-top: 22px !important;}
#head_left {border-bottom: 1px solid #e1e8f5 !important;margin-top:1px!important;}
body.air-theme .navbar-default {
    top:0px!important;
    transition:none!important;
    position: absolute!important;
}

body.air-theme .navbar-default li a {color: #777; font-family: arial, sans serif !important; font-size: 14px;}

div.three_line_menu {background: none!important;}
div.settings_cont {position: absolute!important;}

#head_left {
    width: 100%;
    margin-left: 0px;
    padding: 0;
    font-family: arial,sans-serif;
    max-width: 100%;
    border-bottom: 1px solid #ebebeb!important;
    margin-bottom: 0;
    padding-left: 128px;}

#head_left a {
    margin: 2px 2px 0;
    padding: 0 8px;
    color: #777;
    font-family: arial,sans-serif;
    font-size: small;
    line-height: 54px;
    display: inline-block;
}

#head_left a.active {padding-bottom: 0;}

#head_left .bookmark {top: 4px !important; right: 10px !important;}

#head_left a:hover {border:none!important; padding-bottom: 0; color: #000;}

#head_left .links_n {font-size:small !important;}
  
#head_left .links_n.active{
  font-size: 13px !important;
  font-family:arial,sans-serif!important;
  font-weight:bold!important;
  color:#4285f4!important;
  border-bottom:3px solid #4285f4!important;
  margin:0!important;
  padding:0px !important;
  padding-left:8px !important;
  padding-right:8px !important;
}
  
div#head_left a {color:#777!important;}

/**Search Bar**/

.navbar .navbar-header {
  background-color: #F1F1F1 !important;
  background-image: none!important;
  height:59px!important;
  border-bottom: 1px solid #e5e5e5!important;
  border-top:none!important;
  min-width: 980px;
}

a.navbar-brand {padding: 20px 10px 8px 18px;}

span.clear_search {top: 3px; right:-120px;}
  
div#search {
    padding-top: 5px!important;
    margin-left: 96px;
}

div.search-field {
  border-radius: 0px!important;
  border: 1px solid #d9d9d9;
  height: 38px;
  width: 630px!important;
  max-width: 630px!important;
}
  
div.search-field div.text-fields {width:450px!important;}
span.small-go-btn {height: 38px !important; background: #4387F5}
div#search input#submit1, div#search input#submit1:focus {background: #4D90FE none repeat scroll 0% 0% !important;}
div.search-field .text-fields {padding-right:-50px!important;}

a.navbar-brand img {width: 95px; height: 20px;}
.navbar-brand {padding: 20px 10px 8px 18px;}

/*** Side Bar ***/  
ul.location_filter {border-bottom: none!important;}
ul#by_time li a.active {color:#DD4B39 !important;font-size: 16px !important;font-family:arial,sans-serif!important;font-weight:700!important;}
ul#by_time li a.no-date {color: #000 !important;font-size: 14px !important;font-family:arial,sans-serif!important;}
ul.location_filter li a.active {color:#DD4B39 !important;}
ul.location_filter li a {color:#000;}
.navbar-static-side{
    position:static !important;
    margin-top: 80px;
    bottom: auto;
    top: auto !important;
    left: 0px;
}
.side_list_menu {
    margin: 0 !important;
    padding: 0 !important;
    width: auto;
    border-bottom: 1px solid #e5e5e5;
}

#side_bar2 ul {width: 95px;}
#side_bar2 ul li a.active {color: #4285F4 !important; font-family: arial, san serif !important; font-weight: bold !important; font-size: small !important;}
#side_bar2 ul li a.no-date {color: #777 !important; font-family: arial, san serif !important; font-size: small !important;}
#side_bar2 ul li a.addsettings {color: #777 !important; font-family: arial, san serif !important; font-size: small !important;}
.side_list_menu, #side_bar2 ul#settings {padding-left: 0px !important;}
#side_bar2 ul#settings {margin: 0;}
.side_list_menu, #side_bar2 ul#settings {margin-left: 0px; width: 120px; padding-left: 5px;}

.sidebar-collapse {width: 120px; padding-left:10px; opacity: 0.3; transition: all 1s ease;}
.sidebar-collapse:hover {opacity: 1; transition: all 0.5s ease;}

.sp-bannerSmall_container {display: none;}
  
/*** Advanced Page ***/
  
div.advanced_search_page div#head_left {margin-top: 5px!important;}
div.advanced_search_page div div.navbar-header a.navbar-brand {width:110px!important;height:60px!important;}
.search-field .text-fields {padding-right: 0px!important;}
  
/*** Results ***/
  
div#results {padding-top:0!important;}
#results .result h3 a span.result_url_heading {text-decoration:underline!important;font-family:arial,sans-serif!important;color:#1a0dab!important; font-weight:normal;}
#results .result h3 a:visited span.result_url_heading {color:#551A8B!important;}
div#results .result a.url, #results .result span.url {color:#006621!important;font-size:14px!important; line-height:16px;}
p.desc.clk {font-family:Arial!important;font-size:small!important;color:#545454!important; line-height: 1.4;}
a#proxy_link.proxy {font-family:Arial!important;font-size:14px!important;color:#777!important;}
a.proxy {font-family:Arial!important;font-size:14px!important;color:#777!important;}

div#bottom-result-container {margin-left: 125px;}

  
/*** Search Suggestions ***/
  
div.ac_results {
  margin-top:-22px!important;
  margin-left:-1px!important;
}

div.ac_results:nth-child(3) {
  margin-top:-15px!important;
  margin-left:-12px!important;
}

/*** Settings ***/
 
div.slide-wrapper {
  z-index: 14!important;
  top:120px!important;
}

table.home-wrapper div.slide-wrapper {
  z-index: 14!important;
  top:80px!important;
}
  
.multimedia-div {margin: 19px 0px 0px !important;}

/*** Footer ***/
  
.classified .relatedlist_wrap p a {
  color:#1122CC!important;
}

/*** Main Page ***/
  
body div.cloud {background:none!important;}
div#enhanced {display:none!important;}
span.go-btn {display:none!important;}
  
form#search_form .midline .midline_left p {
  width:430px!important;
  border-radius:2px!important;
}
  
div#links {display: none!important;}
  
div.top_menu {
  width:100%!important;
  margin:0px!important;
  margin-top:7px!important;
  padding-bottom: 7px!important;
  text-align: left!important;
  border-bottom: 1px solid #F7F7F7 !important;
}
  
div.top_menu a {
  font-size:13px!important;
  color:#337fe2!important;
  font-family:arial,sans-serif!important;
  padding-left:4px !important;
  padding-right:4px !important;
}

div.top_menu a#web {margin-left: 10px!important;}
  
div.top_menu a.active {
  color:#DD4B39!important;
  font-family:arial,sans-serif!important;
  font-size:13px!important;
  font-weight:700!important;
  border-top:2px solid #DD4B39!important;
  padding:6px !important;
  padding-left:4px !important;
  padding-right:4px !important;
}
  
div#home_wrap div.settings_cont a#menu-icon-id {
  text-indent: 0px!important;
  background-position: 0px!important;
  position: relative!important;
  top:-20px!important;
  right:-25px!important;
  color:#337fe2!important;
  display: none!important;
}
  
div.top_menu.menu_en #xsearch{display:none;}
div.classified{display:none}
div#footer.footer {
  background-color: #F7F7F7 !important;
  border-top:1px solid #e1e8f5!important;
}

  
div#content a#main_link p#caption {display: none!important;}
div.newLook {display: none!important;}

/*** Images ***/

.transparent-hover {
    background: rgba(51,51,51,0.8) !important;
    font-family:arial,sans-serif !important;
    font-size: 11px;
    font-weight: normal;
    height: 10px;
    line-height: 7px;
}

/*.multimedia_thumb img {margin-right: 0 !important;}*/

span.plus-icon {display:none;}

}
