css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("enigma-wildstar.com") {

/* Backgrounds */
.abbc3_buttons_row, .article-single, #app-form, #app-form > *, #wrap, .loop-wrap, .navbar, .panel, .bg1, .bg2, .sub-panels, #review {
  background:#23272C!important;
  color:#fff!important;
}
.post, .content {
  background: #202328!important;
  color: #fff!important;
}

fieldset, .forum_read, .forum_unread, .display-options {background-color:#23272C !important;}

.announce {
  background:#0029A3!important;
  color:#fff!important;
}

.abbc3_button {background-color: #F0A27A!important;}

textarea, input:not(.abbc3_button), select, form, .hidebox, .spoilwrapper, blockquote {
  background: #15171A!important;
  color: #fff!important;
}

.button, .dropdown-select, .dropdown-contents, .tab *, #navigation a {
  background: #15171A!important;
}

/* Removing black leftovers */
.panel {
  margin-bottom: 0px!important;
  border-radius: 0px!important;
}
.post {border-radius: 0px!important;}

fieldset.submit-buttons, #tabs {margin: 0px!important;}

/* Links */
a:not(.username-coloured) {color: #0094DE!important;}
a.postlink:hover {background: none!important;}

/* Text */
.dropdown-select, .buttons a:hover {text-shadow: none!important;}

.hidebox_visible {color: #597259}
label, h1, h2, h3, .author, dd strong, dd, .signature, .button2, .copyright, .footer-title, .buttons a, .header a, .announce .topictitle, .details dt, .app-form-title-2, .full-width-title, .list-inner, .dropdown-select, .spoilbtn, .spoilcontent, .notice  {color: #fff!important;}


/* Brute force 'cause I don't know how to untarget these properly */
.stat-block em a:nth-child(1) {color: #004080!important;}
.stat-block em a:nth-child(2) {color: #FF8000!important;}
.stat-block em a:nth-child(3) {color: #4080FF!important;}
.stat-block em a:nth-child(4) {color: #0A0!important;}
.stat-block em a:nth-child(5) {color: #FF40FF!important;}
  
}
