css:
@-moz-document domain("readmanga.me"), domain("grouple.ru"), domain("mintmanga.com"), domain("selfmanga.ru") {






* {
    text-shadow: none!important;
}
.navbar {
    background: #00252b;
    margin-top: 6px;
}
body {
    font-size: 14px;
    line-height: 1.22571;
    color: #AE6EA2 !important;
    background: #151515 !important;
}
.top-block .chosen-container .chosen-drop {
    bottom: -165px;
}
a {
    color: #0CF;
    text-decoration: none;
}
.form-control {
    font-size: 14px;
    line-height: 1.18571;
    background-color: #15101D;
    background-image: none;
    border: none;
    border-radius: 4px;
    box-shadow: 0px 1px 1px rgba(98, 0, 255, 0.63) inset;
} 
.btn {
    padding: 4px 21px;
}
.btn-primary {
    color: #AE6E6F;
    background-color: #15101D;
    border-color: #15101D;
} 
.btn-default {
    border-color: #CCC;
    background-color: rgb(5, 5, 5);
    color: #0CF;
}
.second-nav {
    padding-top: 3px;
    background: none;
}
.control-hint {
    background-color: rgb(5, 0, 9);
    border: 2px dotted rgb(0, 204, 255);
    margin-top: 5px;
    padding: 0px 50px;
}   
.pageBlock, .rightContent, #screenshot {
    border-radius: 0px;
}  
.pageBlock {
    background-color:   #ee82ee00;
    border: none !important;
    margin-bottom: 0px;
    padding: 0px;
    box-shadow: none!important;
}
.row {
    margin-left: 5px;
    margin-right: 5px;
}
.federation-nav {
    background: rgba(0, 0, 0, 0.81);
    top: 44px;
}





.panel, .autocomplete-suggestions {
    background-color: #111;
    border: none !important;
}
.panel-footer {
    padding: 7px 13px;
    background-color: #222;
    border-top: none !important;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
}
h1, h3 {
    color: #c7c7c7 !important;
    margin-top: 55px;
}
  
  
  
  

  
  
    
      /**********manga_info**************/
h1.names span:first-child {
    color: #0CF;
} 
h1.names {
    color: #F6F6F6;
}
.manga-description p, .manga-description, .manga-description span {
    color: #0CF !important;
} 
.rightContent {
    background-color: #04192D;
}  
.expandable.expanded .background {
    height: 64px;
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, #050505 55%, #050505 100%) repeat scroll 0% 0% rgba(255, 255, 255, 0);
} 
blockquote {
    background: no-repeat scroll 6px 8px #050505;
    border: 1px dashed #FFF;
    font-size: 0.95em;
    margin: 5px 5px 0px;
    overflow: hidden;
    padding: 5px;
}  
.expandable.expanded .background .open .js-link {
    background: no-repeat scroll left center #050505;
}
.mature-message, .mature-message-small {
    color: #FF366F;
    background-color: #1B0707;
}
.mature-message {
    font-size: 17px;
    margin: 7px -11px 7px -16px;
    padding: 4px 30px;
    border-bottom: none;
    border-top: none;
    border-right: none;
}  
.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    padding: 7px;
    line-height: 1.18571;
    vertical-align: top;
    border-top: 1px solid #291B26;
}
.table > thead > tr > th {
    border-bottom: 3px solid #291B26;
}
.media-body {
    border-bottom: 1px solid #261723;
    padding-bottom: 7px;
}  
.additional {
    color: #E8E8E8;
}
.translator-control {
    margin-top: 5px;
}

  

  
  /**********del**************/ 
iframe,
div[class="manga_ba top_ba"],
div[class="pageBlock container bottom-block"],
div[class="left-branding"],
div[class="right-branding"],
div[class="page-footer"],
a[class="scroll-top hide"],
td[class="vBottom nowrap"]
  {display: none !important;}
}
