css:
@-moz-document domain("pattayaforum.net") {
body {
    background: #6E0000;
}

#uix_wrapper {
    background: #FFFFFF;
}

.sectionMain.ctaFtContainer {
    display: none;
}

.navTabs {
    background: #585858;
    font-weight: normal;
}

.navTabs .navTab.Popup.PopupOpen, .navTabs .navTab.selected.PopupOpen .navLink {
    background: #971507;
    color: #FFFFFF;
}

.navTabs .navTab.PopupClosed .navLink {
    color: #FFFFFF;
}

#userBar .navTabs {
    background: #ECE8E9;
}

.breadcrumb {
    background: #ECE8E9;
}

.breadcrumb .crust:last-child a.crumb {
    color: #000000;
}

.primaryContent a {
    color: #000000;
    text-decoration: underline;
}

h1 {
    color: #000000 !important;
}

a.callToAction span {
    background: #971507;
}

.heading, .xenForm .formHeader {
    background: #ECE8E9;
    color: #000000;
    text-align: center;
}

.discussionList .sectionHeaders {
    background: #971507;
    color: #FFFFFF !important;
    font-weight: bold;
}

.discussionList .sectionHeaders a {
    color: #FFFFFF !important;
}

.discussionList .discussionListItem {
    background: #ECE8E9;
}

.discussionList .discussionListItem.sticky {
    background: #FFFFC8 !important;
}

.discussionListItems li {
    border: 1px solid #CDCDC9 !important;
    border-top: 0 !important;
}

.discussionListItems .title a {
    font-weight: normal;
}

.discussionListItems .title a {
    color: #7D0000 !important;
}

.PageNav a, .PageNav a:hover {
    border: 1px solid #A8A8A8;
}

.PageNav a.text, .PageNav a:hover, .PageNav a:focus {
    color: #000000;
}

.PageNav a.currentPage {
    background: #A8A8A8 !important;
    color: #000000;
    font-weight: bold;
}

.sectionFooter {
    background: none !important;
    border: 0 !important;
}

#uix_footer_columns .pageContent {
    background: #ECE8E9;
    border: 1px solid #CDCDC9 !important;
}

#uix_footer_columns .uix_footer_columns_container > li ul.footerMenu>li>a {
    color: #7D0000 !important;
}

#uix_jumpToFixed {
    display: none !important;
}

.node .unread .nodeText .nodeTitle, .subForumList a, .node .unread .nodeIcon.hasGlyph, .sidebar .visitorPanel .username, .discussionListItems .unread .lastPostInfo .username, .message .messageMeta .uix_postbit_privateControlsMenu {
    color: #7D0000 !important;
}

.button.primary, .newIndicator, .message .messageMeta .uix_postbit_privateControlsMenu.PopupOpen, .uix_nodeTitle_status {
    background: #7D0000 !important;
    color: #FFFFFF !important;
}

.message .messageMeta .control {
    color: #000000;
    text-decoration: none;
}

.message .signature {
    border-top: 1px solid #CDCDC9;
}

.messageList .message {
    border: 1px solid #CDCDC9 !important;
}

iframe body {
    background: #FFFFFF;
}

.messageText {
    font-family: Verdana;
    font-size: 12px;
}

.userBanner.bannerOlive, .userBanner.bannerOlive.wrapped span {
    background: #444444;
}

.userBanner {
	color: #FFFFFF !important;
}

.thread_view .threadAlerts {
    background: #ECE8E9;
}
}
