#main-room-active-link .room-name:after {
    content: " Sample text";
    display: block;
    position: absolute;
    margin-top: -24px;
    margin-left: 166px;
}
*/
/* Bottom bar */
/*@media screen and (min-width: 72em) {}*/
#player-controller {
    /*background: linear-gradient(to top, rgba(20,20,20,0.5) 0%, rgba(20,20,20,0.25) 60%, rgba(20,20,20,0) 100%);*/
    background: rgba(15,15,15,0.42);
}
#player-controller .left {
    /*background: linear-gradient(to top, rgba(20,20,20,0.5) 0%, rgba(20,20,20,0.30) 60%, rgba(20,20,20,0) 100%);*/
    background: rgba(15,15,15,0.42);
}
#player-controller .right {
    background: rgba(0,0,0,0);
}
#player-controller .left ul li.imgEl {
    cursor: pointer;
}
#player-controller .imgEl img {
    border-radius: 100%;
    margin: 5px;
    max-width: 83%;
}
#player-controller .left ul li.infoContainer .infoContainerInner {
    padding: .5rem 5rem .5rem 1rem;
}
#player-controller .left ul li.infoContainer .infoContainerInner .currentDJSong {
    color: #888;
    font-size: .95rem;
}
#player-controller .left ul li.infoContainer .infoContainerInner .currentTime,
#player-controller .left ul li.infoContainer .infoContainerInner .currentSong {
    color: #ddd;
}
#player-controller .left ul li.infoContainer .infoContainerInner .progressBg {
    background: #5A93CC;
    border-radius: 1rem;
    left: 1rem;
}
#player-controller .right ul li a.dubup:hover,
#player-controller .right ul li a.dubup.voted,
#player-controller .right ul li a.dubdown:hover,
#player-controller .right ul li a.dubdown.voted,
#player-controller .right ul li a.add-to-playlist-button.grabbed {
    background: rgba(0,0,0,0);
}
#player-controller .right ul li a.dubup.voted span,
#player-controller .right ul li a.dubup:hover:not(.voted) .icon-arrow-up {
    color: #93CC5A;
}
#player-controller .right ul li a,
#player-controller .right ul li a.dubup:hover:not(.voted) span,
#player-controller .right ul li a.dubup .icon-arrow-up,
#player-controller .right ul li a.dubdown:hover:not(.voted) span,
#player-controller .right ul li a.dubdown .icon-arrow-down,
#player-controller .right ul li a.add-to-playlist-button.grabbed span,
#player-controller .right ul li a.add-to-playlist-button:hover span,
#player-controller .right ul li a span.icon-heart:before {
    color: #ccc;
}
#player-controller .right ul li a.dubdown.voted span,
#player-controller .right ul li a.dubdown:hover:not(.voted) .icon-arrow-down {
    color: #FF4060;
}
#player-controller .right ul li a:hover span.icon-heart:before,
#player-controller .right ul li a.add-to-playlist-button.grabbed span,
#player-controller .right ul li a.add-to-playlist-button.grabbed .icon-heart:before {
    color: #CC5A93;
}
#player-controller .right ul li .display-browser:hover .icon-playlist {
    color: #5A93CC;
}


/* Right side menu */
#private-messages .loading {
    background: rgba(50,50,50,1);
    color: #eee;
    border-bottom-left-radius: .5rem;
    border-bottom-right-radius: .5rem;
}
#private-messages .main-message-list, 
#private-messages .message-details {
    background: linear-gradient(to bottom, rgba(15,15,15,0.75) 0%, rgba(30,30,30,0.95) 10%, rgba(15,15,15,0.95) 90%, rgba(15,15,15,0.66) 100%);
}
#main-menu-right .close-menu {
    background: linear-gradient(to bottom, rgba(25,25,25,1) 0%, rgba(25,25,25,1) 175%);
}
#main-menu-right .close-menu:hover span {
    color: #fff;
}
#main-menu-right .close-menu .icon-close {
    padding: 0 0 0 2px;
    color: #aaa;
}
#main-menu-right .close-menu .icon-close:before {
    content: "\e800";
    line-height: 1.9;
    font-size: 20px;
    padding-left: 1px;
}
#private-messages .message-details .message-header {
    padding: 1em 1em;
    border-bottom: 1px solid #333;
    background: #1a1a1a;
    z-index: 12;
}
#private-messages .main-message-list .message-header, 
#private-messages .message-details .message-header {
    background: rgba(25,25,25,1);
    border-bottom: 1px solid #222;
}
#private-messages .main-message-list .message-header input, 
#private-messages .message-details .message-header input {
    border: 1px solid rgba(40,40,40,1);
}
#private-messages .main-message-list ul.message-list li.message-item, 
#private-messages .message-details ul.message-list li.message-item {
    border-bottom: 1px solid #222;
}
#private-messages .main-message-list .message-header a, 
#private-messages .message-details .message-header a {
    color: #aaa;
    font-size: 1rem;
}
#private-messages .main-message-list .message-header a:hover, 
#private-messages .message-details .message-header a:hover {
    color: #eee;
}
#private-messages .main-message-list ul.message-list li.message-item .message-content h3, 
#private-messages .message-details ul.message-list li.message-item .message-content h3 {
    color: #5A93CC;
}
#private-messages .main-message-list ul.message-list li.message-item.new-message:before, 
#private-messages .message-details ul.message-list li.message-item.new-message:before {
    background: #5A93CC;
}
#private-messages .private-message-details-info, 
#private-messages .private-message-info {
    color: #aaa;
}
#private-messages .main-message-list ul.message-list li.message-item .message-content .username-handle, 
#private-messages .message-details ul.message-list li.message-item .message-content .username-handle {
    color: #eee;
    font-weight: 400;
}
#main-message-list .search-results .search-results-users .result-item {
    background: rgba(20,20,20,1);
}
#main-message-list .search-results .search-results-users .result-item:hover {
    background: #333;
    color: #fff;
}

/* Left side Menu and Lobby */
#main-menu-left {
    padding: 5rem 0 0;
    background: linear-gradient(to bottom, rgba(20,20,20,0.66) 0%, rgba(20,20,20,0.75) 10%, rgba(20,20,20,0.75) 90%, rgba(20,20,20,0.66) 100%)!important;
    height: calc(100% - 114px);
    left: -224px;
    left: -14rem;
    top: 58px;
    width: 215px;
}
#main-menu-left a,
#main-menu-left a span,
#main-menu-left a.room-active-link {
    color: #ccc;
}
#main-menu-left a:hover,
#main-menu-left a span:hover,
#main-menu-left .close-menu span:hover {
    color: #fff;
}
/* Lobby */
#room-list .room-loby-header {
    background: rgba(20,20,20,0.66);
    border-top: 1px solid #878c8e;
}
#room-list .room-loby-header .links-container a {
    color: #ddd;
}
#room-list .room-loby-header .links-container a:hover,
#room-list #container-room-list .room-item header:hover {
    color: #fff;
    cursor: pointer;
}
#room-list #container-room-list .room-item .roomImage .join {
    background: rgba(200,200,200,0.5);
    color: #ddd;
    text-shadow: #222 2px 2px 1px;
}
#roomFormUpdate form .modal-footer.mainForm button:hover {
    background: #5A93CC;
    color: #222229;
}

/* Chat container */
#main-room .right_section {
    background: rgba(15,15,15,0.66);
    border-radius: 0.5rem;
}
#chat .chat_tools span:after {
    border: 1px solid #ddd;
}
#chat .chat_tools .display-chat,
#main-room.display-users-rooms #chat .display-room-users,
#main-room.display-chat-settings #chat .display-chat-settings,
#chat .chat_tools .icon-chatsettings:hover,
#chat .chat_tools .display-room-users.icon-people:hover,
#main-room.display-users-rooms #chat .display-chat:hover, 
#main-room.display-users-rooms #chat .display-chat-settings:hover,
#main-room.display-chat-settings #chat .display-chat:hover,
#main-room.display-chat-settings #chat .display-room-users:hover,
#chat .chat-container .pusher-chat-widget-input .icon-camera:hover {
    color: #fff;
}
#chat .chat-container .pusher-chat-widget-input .icon-camera {
    color: #aaa;
}
#chat .pusher-chat-widget-input #new-messages-counter {
    background: #5A93CC;
    color: #222229;
    opacity: 1;
}
#chat .chat-container ul.chat-main li .chatDelete,
#chat .chat-container ul.chat-main li:focus .chatDelete, 
#chat .chat-container ul.chat-main li:hover .chatDelete {
    color: #ddd!important;
    opacity: 1;
    font-size: 0.65rem;
    padding-left: 2rem;
    padding-right: 1rem;
    padding-bottom: 1rem;
    padding-top: 0.2rem;
}
#chat .chat-container ul.chat-main li .chatDelete:hover {
    color: #FF4060!important;
}
    /* Chat messages */
#chat .chat-container .chat-messages .chat-main li.chat-welcome-message a {
    color: #5A93CC;
}
#chat .chat-container .chat-messages .chat-main li.chat-system-grabbed,
#chat .chat-container .chat-messages .chat-main li.chat-system-joined,
#chat .chat-container .chat-messages .chat-main li.chat-system-left,
#chat .chat-container .chat-messages .chat-main li.chat-system-loading,
#chat .chat-container .chat-messages .chat-main li.system, 
#chat .chat-container .chat-messages .chat-main li.system-error {
    font-size: 0.9em;
    font-weight: 700;
    opacity: 1;
    text-align: center;
    text-shadow: 0px 0px 1px rgba(0,0,0,0.3);
}
#chat .chat-container .chat-messages .chat-main li.system,
#chat .chat-container .chat-messages .chat-main li.chat-system-loading,
#chat .chat-container .chat-messages .chat-main li.system-error,
.system-mute,
.system-unmute {
    color: #0ff!important;
}
.system-error {
    color: #f0f;
}
.chat-system-joined,
.system-join,
.system-userjoin {
    color: #5A93CC!important;
}
.chat-system-left,
.system-leave,
.system-userleave {
    color: #935ACC!important;
}
.system-dubup,
.system-userudub {
    color: #93CC5A!important;
}
.system-dubdown,
.system-userddub {
    color: #FF4060!important;
}
.chat-system-grabbed,
.system-grab {
    color: #CC5A93!important;
}
.system-resume,
.system-pause {
    color: #5ACC93!important;
}
#chat .chat-container .chat-messages .chat-main li {
    font-size: .9rem;
    padding: 0 0 1rem;
    overflow: hidden;
}
#chat .chat-container .chat-messages .chat-main li .activity-row .text p {
    color: #ddd;
    opacity: 1;
}
#chat .chat-container .chat-messages .chat-main li .activity-row .text img {
    max-height: 400px;
}
#chat .chat-container .chat-messages .chat-main li.chat-me-command-joined {
    color: #aaa;
}
#chat .chat-container ul.chat-main li .text a.autolink,
#chat .chat-container ul.chat-main li .activity-row .text p a.autolink,
#chat .chat-container ul.chat-main li .activity-row .text p a.autolink:visited:hover,
#chat .chat-container .chat-messages.ps-container ul.chat-main li.chat-me-command-joined .text a:hover,
#chat .chat-container .chat-messages.ps-container ul.chat-main li.chat-me-command-joined .text a:visited:hover {
    color: #5A93CC;
    font-weight: 400;
}
#chat .chat-container ul.chat-main li .activity-row .text p a.autolink:hover,
#chat .chat-container ul.chat-main li .activity-row .text p a.autolink:visited,
#chat .chat-container .chat-messages.ps-container ul.chat-main li.chat-me-command-joined .text a:visited {
    color: #0063a6;
    font-weight: 400;
}
#chat .chat-container .chat-messages .chat-main li .activity-row .text .username-handle {
    color: #fff;
    opacity: 1;
    font-weight: 400;
}
#chat .chat-container .chat-messages .chat-main li .text .username-handle[style="color: rgba(255, 0, 255, 0.8);"] {
    color: #FFDD6F!important;
}

    /* Usernames */
#chat .chat-container .chat-messages .chat-main li .activity-row .text a.username, 
#chat .chat-container .chat-messages .chat-main li .activity-row .text a.username:visited {
    display: block;
    font-size: .9rem;
    line-height: 1.2;
    font-weight: 400;
    opacity: 1;
}
#chat .chat-container .chat-messages .chat-main li .activity-row .text a.username:hover {
    color: #fff;
}
#chat .chat-container .chat-messages .chat-main li.current-chat-user .activity-row .text p a.username {
    color: #FFDD6F;
}

    /* Special Users */
#chat .chat-container ul.chat-main li.user-5600e1bf2e803803000fee40 .activity-row .text p a.username, /* Roomba */
#chat .chat-container ul.chat-main li.user-56208ef772fbd14400119c64 .activity-row .text p a.username, /* Roomba new */
#chat .chat-container ul.chat-main li.user-55ff1b594c828c03008bd5f7 .activity-row .text p a.username { /* Chillbot */
    color: #5A93CC!important;
    font-weight: 600!important;
    font-size: 1em;
    text-shadow: 0px 0px 1px rgba(0,0,0,0.5);
}
#chat .chat-container ul.chat-main li.user-56208ef772fbd14400119c64 .activity-row .text p a.username:before { /* Roomba new */
/*     content: url(https://rawgit.com/ChilloutMusica/dubtheme/master/icons/roomba.png); */
    position: relative;
    top: 3px;
    padding-right: 4px;
}
#chat .chat-container .chat-messages .chat-main li.isCo-owner .activity-row .text p .username, 
#chat .chat-container .chat-messages .chat-main li.isCo-owner .activity-row .text p .username:visited {
    color: #d3dbe0;
}
#chat .chat-container .chat-messages .chat-main li.isManager .activity-row .text p .username, 
#chat .chat-container .chat-messages .chat-main li.isManager .activity-row .text p .username:visited {
