}
#chat .chat-container .chat-messages .chat-main li.isMod .activity-row .text p .username,
#chat .chat-container .chat-messages .chat-main li.isMod .activity-row .text p .username:visited {
    color: #d3dbe0;
}
#chat .chat-container .chat-messages .chat-main li.isVip .activity-row .text p .username, 
#chat .chat-container .chat-messages .chat-main li.isVip .activity-row .text p .username:visited {
    color: #a3abaf;
}
#chat .chat-container .chat-messages .chat-main li.isResident-dj .activity-row .text p .username, 
#chat .chat-container .chat-messages .chat-main li.isResident-dj .activity-row .text p .username:visited {
    color: #999;
}
#chat .chat-container .chat-messages .chat-main li.isDefault .activity-row .text p .username, 
#chat .chat-container .chat-messages .chat-main li.isDefault .activity-row .text p .username:visited {
    color: #999;
}
#chat .chat-container .chat-messages .chat-main li.isCo-owner .activity-row .text p .username:hover, 
#chat .chat-container .chat-messages .chat-main li.isManager .activity-row .text p .username:hover, 
#chat .chat-container .chat-messages .chat-main li.isMod .activity-row .text p .username:hover, 
#chat .chat-container .chat-messages .chat-main li.isVip .activity-row .text p .username:hover, 
#chat .chat-container .chat-messages .chat-main li.isResident-dj .activity-row .text p .username:hover, 
#chat .chat-container .chat-messages .chat-main li.isDefault .activity-row .text p .username:hover {
    color: #eee;
}

