/* User tab */
#main_avatar_room #main-user-list-room #avatar-list li p.username{
    color: #aaa;
}
#main_avatar_room #main-user-list-room #avatar-list li p.username:hover,
#main_avatar_room #main-user-list-room #avatar-list li.admin p.username:after, 
#main_avatar_room #main-user-list-room #avatar-list li.co-owner p.username:after, 
#main_avatar_room #main-user-list-room #avatar-list li.creator p.username:after, 
#main_avatar_room #main-user-list-room #avatar-list li.currentDJ p.username:after, 
