#main_avatar_room #main-user-list-room #avatar-list li.vip p.username:after, 
#main_avatar_room #main-user-list-room #avatar-list li.dj p.username:after, 
#main_avatar_room #main-user-list-room #avatar-list li:after p.username:after {
    color: #fff;
}

/* Options tab */
#chat .chat-options .chat-option-header {
    font-size: 1rem;
    color: #ddd;
}
#chat .chat-options .chat-option-buttons span {
    background: rgba(15,15,15,0.25);
    color: #aaa;
}
#chat .chat-options .chat-option-buttons span:hover {
    background: rgba(50,50,50,0.25);
    color: #fff!important;
}
#chat .chat-options .chat-option-buttons span.active {
    background: rgba(50,50,50,0.25);
}
#chat .chat-options .chat-option-buttons span.active i {
    color: #fff;
}

/* User popup */
#user_popover {
    border-radius: 1rem;
    background: rgba(40,45,50,0.95);
}
#user_popover .usercontent header h3 {
    color: #eee;
}
#user_popover .usercontent header .dubs {
    color: #ccc;
}
#user_popover .actions,
#user_popover .global-actions {
    background: rgba(50,55,60,0.4);
}
#user_popover .actions a,
#user_popover .global-actions a {
    color: #aaa;
    border-top: 1px solid #444;
    outline: none;
    font-weight: 400;
}
#user_popover .actions a:hover,
#user_popover .global-actions a:hover {
    background: rgba(65,70,75,0.4);
    color: #fff;
}
#user_popover .actions a input {
    vertical-align: middle;
    border-radius: 0rem;
    border: 1px solid #55555a;
    background: rgba(30,30,30,0.4);
    color: #ddd;
    padding: 2.4px 8px;
    padding: .15rem .5rem;
    width: 64px;
    width: 4rem;
}

/* Video (Main player - left) */
    /* Header */
#main_player {
    background: rgba(15,15,15,0.66);
    border-radius: 0.0rem;
    border-top: 1px solid #878c8e;
    border-bottom-left-radius: 0.5rem;
    border-bottom-right-radius: 0.5rem;
}
#main_player .player_header {
    background: rgba(20,20,20,0.66);
    color: #ccc;
    opacity: 1;
    border-radius: 0.0rem;
    top: -41px;
    border-top-left-radius: 0.5rem;
    border-top-right-radius: 0.5rem;
}
#main_player .player_header span {
