css:
@-moz-document domain(dobrochan.com)
{
html, body {
	background: scroll #211F1A url('http://static.wixstatic.com/media/2b0659_59315cd535774b499297689c9557371c.jpg') repeat;
	color: #9B8165;
	font-family: Verdana,sans-serif;
	font-size: 1em;
}
::-moz-selection{background:#FEC77D!important;color:#111111!important}
::selection{background:#FEC77D!important;color:#111111!important}
input[type="submit"],input[type="reset"], input[type="button"]{-o-appearance:none!important;-webkit-appearance:none!important;-moz-appearance:none!important;appearance:none!important;background-color:#333333!important;background-image:-moz-linear-gradient(center top,#4A4A4A,#292929,#4A4A4A)!important;background-image:-o-linear-gradient(rgb(74,74,74),rgb(41,41,41),rgb(74,74,74))!important;background-image:-webkit-gradient(linear,center top,center bottom,from(#4A4A4A),color-stop(25%,#313131),color-stop(50%,#292929),color-stop(75%,#313131),to(#4A4A4A))!important;-webkit-border-radius:5px!important;-moz-border-radius:5px!important;border-radius:5px!important;border-bottom:1px solid #151515!important;border-top:1px solid #151515!important;border-left:1px solid #000000!important;border-right:1px solid #000000!important;color:#AAAAAA!important;}
input[type="submit"]:hover,input[type="reset"]:hover, input[type="button"]:hover{background-color:#212121!important;background-image:-moz-linear-gradient(center top,#151515,#505050,#151515)!important;background-image:-o-linear-gradient(rgb(21,21,21),rgb(80,80,80),rgb(21,21,21))!important;background-image:-webkit-gradient(linear,center top,center bottom,from(#151515),color-stop(25%,#222222),color-stop(50%,#505050),color-stop(75%,#222222),to(#151515))!important;border:1px solid #9B8165!important;color:#FFFFCC!important;box-shadow:inset 0 0 4px 1px  #9B8165}
input::-moz-focus-inner { 	border: 0; 	padding: 0; 	margin-top:0; 	margin-bottom: 0; }
font[color*="2267"] em{color:#EE0000!important;font-weight:bold}
hr {
	height: 3px!important;
	border: 0!important;
	background: url('http://static.wixstatic.com/media/2b0659_4f5f3c1f16fe407b85596898fa50f523.png') center no-repeat;
	background-size: 100% 100%;
	-moz-background-size: 100% 100%;
}
	hr{
	border-style:none none solid!important;
	border-color:rgba(0,0,0,.3)!important;
	box-shadow:0 0px 0 #fff!important;
}
select, textarea, .mselect, input[type="text"], input[type="file"], input[type="password"], input[name="snum"], #anoniconsselectlist {
	color: #FEC77D!important;
	background: #3E3C38 url('http://static.wixstatic.com/media/2b0659_c77b18ab3dd0463e89e52fa46b3b31ce.jpg') !important;
	border: 1px solid #44453D;
	border-radius: 5px;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	-o-transition: all 0.3s ease;
	-webkit-transition: all 0.3s ease;
	-moz-transition: all 0.3s ease;
	transition: all 0.3s ease;
}
 a, #adminbar ul.menu ul li a, .rmenu a, .a-link-emulator,span.spoiler:hover a {
	color: #FFD97A;
	text-decoration: none;
	-o-transition: all 0.3s ease;
	-moz-transition: all 0.3s ease;
	-webkit-transition: all 0.5s ease;
	transition: all 0.3s ease;
}
a:hover, #adminbar ul.menu ul li a:hover, .rmenu a:hover, .a-link-emulator:hover {color: #FCE236;text-shadow: #FCE236 0px 0px 10px;}
#ABU-select{border:solid 1px #9B8165!important;box-shadow:0 0 4px rgba(155,129,101,1);background:#3E3C38 url('http://static.wixstatic.com/media/2b0659_c77b18ab3dd0463e89e52fa46b3b31ce.jpg')}
#ABU-select a{-o-transition:none;-moz-transition:none;-webkit-transition:none;transition:none}
#ABU-select a:hover{color:#FFD97A;text-shadow:none;background:url('http://static.wixstatic.com/media/2b0659_5f091821755a4a20a2d1f3f05015a79f.jpg') #292825!important}
#ABU-select a:hover:first-child{border-radius:5px 5px 0 0}
#ABU-select a:hover:last-child{border-radius:0 0 5px 5px}
#ABU-alert_wait{border:solid 1px #34352D!important;background:#292825!important;-o-transition:all 05s ease;-moz-transition:all 0.5s ease;-webkit-transition:all 0.5s ease;transition:all 0.5s ease}
#ABU-alert {border-color:#C6985C!important}
a.postbtn-hide{background-image:url("data:image/gif;base64,")!important}
span.postbtn-options{background-image:url("data:image/gif;base64,")!important}
a.postbtn-expall{background-image:url("data:image/gif;base64,")!important}
a.postbtn-exp{background-image:url("data:image/gif;base64,")!important}
a.postbtn-adm{background-image:url("data:image/gif;base64,")!important}
.post .post-title {color: #FFC17B;}
.reply, .pager, .boardstats, .dropd-board-btn, .popup {
	color: #C6985C;
	background: url('http://static.wixstatic.com/media/2b0659_5f091821755a4a20a2d1f3f05015a79f.jpg') #292825;
	border: solid 1px #34352D !important;
	border-radius: 5px;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	-moz-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
	-webkit-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
	box-shadow: 0px 0px 12px #161616;
}
.reply.hiclass {
	color: #C6985C;
	background: url('http://static.wixstatic.com/media/2b0659_5f091821755a4a20a2d1f3f05015a79f.jpg') #292825;
	border: solid 1px #34352D !important;
	padding: 1px 2px 6px 2px;
	border-radius: 5px;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	-moz-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
	-webkit-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
	box-shadow: 0px 0px 12px #161616;
}
.unkfunc {background: inherit;color: #C1B387;}
a[href="mailto:sage"], a[href="mailto:Sage"], a[href="mailto:SAGE"] {color: #AA0000!important;}
a[href="mailto:sage"]:before, a[href="mailto:Sage"]:before, a[href="mailto:SAGE"]:before {content: url('http://static.wixstatic.com/media/2b0659_b72778535ef848fa9050ae2833ef2b93.png')!important;}

span.spoiler, span.spoiler a{background: #454545;color: #454545;opacity: .5;}
span.spoiler:hover {color: #AEAEAE;opacity: 1;}
span.mod{color:#FFAB3F!important}
span.adm{color:#EE0000!important}
.mess-post {color: #896640;}
.pomyanem {color: #EE0000!important;font-weight: bold;}
.markup {opacity: 0;padding: 5px;}
.m-bold,.m-italic,.m-quote,.m-underline,.m-overline,.m-spoiler,.m-strike,.m-sup,.m-sub,.m-hidden{height:27px;width:27px;display:inline-block}
.m-spoiler,.m-hidden{width:41px!important}
