@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("silicon.com") {

/* logon box, if you've signed up you may want to remove this line*/
/* .topPsn{display: none !important;} */

/*ads */
#elu_div, #elu, #vs_promo_box, #sky, #topAd ,#vs_promo_sponsor ,#vs_promo_sponsored {display: none !important;}
.topad, a[href*="doubleclick.net"]  ,.textBox { display: none !important; }

/* lots of extra stuff */
.rightCol ,#top3bx ,.mainColNewsletterColumns ,.mainColNewsletterSubmit ,.mainColNewsletterLastColumn ,#mainColNewsletters ,.hdrTopNavPromo ,.award ,.topPsnRight {display: none !important;}


/*extra ads */
iframe[src='http://bwp.silicon.com/search'] {display: none !important;}
script[src='.doubleclick.net'] {display: none !important;}
img[src='.doubleclick.net']{display: none !important;}
[img*="/doubleclick.net"]{display: none !important;}


/*newletter headder image */
img[alt='Newsletters']{display: none !important;}

.leftColImgIconTextLinks {display: none !important;}

.leftColStory , .leftColAuthorName {width:700px !important;}

}
