css:
@-moz-document domain("wikipedia.org") {

/* Modern Wikipedia
   by Adanedhel
   v 1.0
*/

body {
  background-color: #f1f1f1 !important;
}

.bodyContent {
  max-width: 940px;
  margin: 0 auto;
}

.mw-content-ltr {
  background-color: #ffffff !important;
  padding: 40px !important;
  border: 1px solid #c7c7c7 !important;
  max-width: 940px;
  margin: 0 auto;
}

.content {
  background-color: #f1f1f1 !important;
  border: 0 !important;
}

.mw-body {
  border: none;
  padding: none;
  margin: none;
  background-color: #f1f1f1 !important;
}

.firstHeading {
  font-family: 'helvetica' !important;
  color: black;
  font-size: 2.5em !important;
  text-align: center;
  border: none !important;
}

p {
  font-family: 'helvetica';
  color: black;
  font-size: 1em;
  line-height: 1.2 em;
  text-align: justify;
}

a, .body ul li a {
  font-family: 'helvetica';
  font-weight: bold;
  padding: 2px;
  color: #5A8FC2;
  border-radius: 4px;
  font-size: 1em;
  line-height: 1.2 em;
}
  
a:visited, .body ul li a:visited {
  color: #5A8FC2;
}
  
a:hover, .body ul li a:hover {
  background-color: #C9DCF9;
  text-decoration: none;
}

a:active, .body ul li a:active {
  background-color: #5A8FC2;
  color: white;
  text-decoration: none; 
}

.toc {
  background-color: white !important;
  border: none !important;
  padding: none;
  margin: none;
  margin-top: 20px;
  margin-bottom: none;
}

.toc h2, .toctoggle {
  display: none !important;
}

.hatnote {
  paddling: 0;
  margin: 0;
}

.infobox geography card {
  border: none !important;
  background-color: #F0F0FA !important;
  position: fixed !important;
  width: 250px !important;
  top: 0 !important;
}

}
