css:
@-moz-document domain("vulcun.com") {
a:link{color:#A6FF00 !important}
    a:visited{color:#C4FF00 !important}
a:hover {color:#797878 !important;}
    a:active{color:#E6FF00 !important}






body {
    background-color: rgb(0, 0, 0) !important;
}


body {
    color: #047934 !important;
}


/*headline*/
#vu-game-listing{
    background: rgb(33, 33, 33) !important;

}

/*za kurc*/

.table>caption+thead>tr:first-child>td, .table>caption+thead>tr:first-child>th, .table>colgroup+thead>tr:first-child>td, .table>colgroup+thead>tr:first-child>th, .table>thead:first-child>tr:first-child>td, .table>thead:first-child>tr:first-child>th {
    border-top: 0;
    background-color: #000601 !important;
}


.goldp:hover, div.video-container:hover {
    cursor: pointer;
}



.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 2px solid #089E2F !important;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: middle;
    border-top: 2px solid #0C2F19 !important;
}





/*matches*/
.match-list__match {
    background: #1A2921 !important;
    width: 45px;
    margin: .2em;
    padding: 5px 0;
    border-radius: 5px;
    line-height: 12px;
}

/*result panel header*/
.panel-default>.panel-heading {
    color: #576077 !important;
    background-color: #3B3C42 !important;
    border-color: #060606 !important;
}

/*result panel*/
.fantasy-contest__info, .fantasy-contest__results {
    background-color: #122D1A !important;
}

/*succes message*/
.alert-success {
    background-color: #283522 !important;
    border-color: #17B104 !important;
    color: #3c763d !important;
}

/*star player*/
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: middle;
    border-top: 2px solid #0C2F19 !important;
}

/*bar*/
.contest-status-nav {
    border-bottom: 1px solid #1D3E0D !important;
}

/*rezulti*/
.contest-status-nav>li a {
    background: -webkit-linear-gradient(top, #1D2913 0%,#61c419 50%,#1A2D0B 100%) !important;
}


/*warning alert notification*/
.alert-warning {
    background-color: #02520C !important;
    border-color: #32C129 !important;
    color: #000000 !important;
}
/*link u alert warning*/
.alert-link {
    color: #02FF00 !important;
}

/*listinzi*/
.info-panel ul, .table-lobby tbody td:first-child, .table-lobby thead th:first-child {
    background: -webkit-linear-gradient(top, #1D2913 0%,#000000 50%,#1A2D0B 100%) !important;
}


/*slova*/
.table {
    width: 100%;
    max-width: 100%;
    margin-bottom: 20px;
    color: #089A2F;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    background: -webkit-linear-gradient(top, #1D2913 0%,#000000 50%,#1A2D0B 100%) !important;
    border-top: 2px solid #0C2F19 !important;
}

/*rank panel*/
.default-panel {
    padding: 5px;
    text-align: center;
    background: -webkit-linear-gradient(top, #000000 0%,#247D24 17%,#1A5A1A 33%,#1B611B 67%,#182518 83%,#002400 100%) !important;
}

/*exp bar*/
.progress {
    background-color: rgb(14, 64, 1) !important;
}

/*jackpot item*/
.jackpot-item {
    background: -webkit-linear-gradient(top, #035209 0%,#000000 17%,#19C104 33%,#08B718 67%,#035209 83%,#000000 100%) !important;
}


/*jackpot winners*/
.jackpot-winners__winner {
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
    -ms-flex-direction: column;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    /* background: #333C18; */
    padding: 5px;
    background: -webkit-linear-gradient(top, #02440A 0%,#000000 17%,#000000 33%,#000000 67%,#000000 83%,#10FF00 100%) !important;
    text-align: center;
    box-shadow: 0 1px 3px rgba(0,0,0,.12),0 1px 2px rgba(0,0,0,.24);
}


/*lobby*/
.row {
    background-color: black !important;
}
/*lobby header*/
.fantasy-contest__header {
    background: -webkit-linear-gradient(top, #00FF1F 0%,#000000 17%,#000000 33%,#115014 67%,#000000 83%,#10FF00 100%) !important;
}
.modal-header {
    padding: 15px;
    border-bottom: 1px solid #04FF00;
    min-height: 16.43px;
}

/*okvir*/
.modal-body {
    color: cyan;
    background: -webkit-linear-gradient(top, #00580B 0%,#012F00 17%,#005416 33%,#115014 67%,#003304 83%,#055600 100%) !important;
}
/*okvir gornji*/
.modal-header {
    background-color: black !important;
}


/*broj*/
.trivia-wager__input {
    border: 2px solid #0045F9 !important;
}
button, input, select, textarea {
    background-color: #0A5F00 !important;
}

/*options*/
.trivia-options a {
    border: 2px solid #ccc !important;
    background: #000 !important;
    border-radius: 6px;
}


.tab-nav li>a {
    background-color: #272E48 !important;
}
.info-panel {
    background-color: #212515 !important;
    padding: 15px;
}
.photo-testimonials {
    background-color: #2A2C33 !important;
}
#openings {
    background: #232121 !important;
}
.grow-with-us {
    background-color: #191515 !important;
}

/*chat header*/
.vlch-widget .vlch-header__middle a {

    background-color: black !important;

}

.vlch-widget .vlch-sub-header, .vlch-widget .vlch-sub-header-dropdown__search {
    color: #0062F5 !important;
    background-color: black !important;
}


/*chat*/
.vlch-widget .vlch-message__item {
    border-bottom: 2px solid #02A507 !important;
}
.vlch-widget .vlch-message {
    background-color: #000 !important;
}


.vlch-widget .vlch-message__body {
    white-space: pre-line;
    color: #FFFFFF !important;
    max-width: 200px;
    word-wrap: break-word;
}

.vlch-widget .vlch-header {
    transition: background .2s ease;
    background: #172F88 !important;
    border-bottom: 2px solid #010617 !important;
    border-top: 2px solid #000806 !important;
}

.vlch-widget .vlch-chat {
    background: #043312 !important;
    border-left: 3px solid #205F2D !important;
}

.vlch-widget .vlch-footer {
    padding: 0;
    border-top: 3px solid #000000 !important;
    border-top-width: 3px;
    border-top-style: solid;
    border-top-color: rgb(0, 0, 0);
    border-bottom: 3px solid #000000 !important;
    border-bottom-width: 3px;
    border-bottom-style: solid;
    border-bottom-color: rgb(0, 0, 0);
}

.vlch-widget a {
    background-color: rgb(39, 39, 39) !important;
}


input[type=text].input--secondary, input[type=tel].input--secondary, input[type=email].input--secondary, input[type=password].input--secondary {
    color: #FFFFFF!important;
}

.vlch-widget .vlch-rooms-dropdown {
    background: #000000 !important;
}



.vlch-widget .vlch-sub-header, .vlch-widget .vlch-sub-header-dropdown__search {
    position: relative;
    color: #000000;
    border-bottom: 1px solid #00FF08 !important;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(0, 255, 8);
    height: 45px;
    font-size: 14px;
}



.live-panel__menu {

    background: #004827 !important;
    border-bottom: 1px solid rgb(41, 121, 0) !important;
}



.live-panel__tab {
    background: #0D8810 !important;

    background-color: rgb(13, 136, 16) !important;
}


.nav-tabs>li>a {
    margin-right: 2px;
    line-height: 1.42857143;
    border: 1px solid rgb(0, 0, 0) !important;
    border-top-color: rgb(0, 0, 0);
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: rgb(0, 0, 0);
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: rgb(0, 0, 0);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: rgb(0, 0, 0);
    border-left-style: solid;
    border-left-width: 1px;
    border-radius: 4px 4px 0 0;
}


.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #555;
    background-color: #029000 !important;
    border-bottom-color: rgb(0, 90, 7);
    cursor: default;
}

.btn-default {
    color: #333!important;
    background-color: #211C1C!important;
    border-color: #37CE06!important;
}


ul#fundamount-funds li a {
    background: #585858 !important;
}

.team-roster li.playeradded {
    border: 2px solid #C5FF06 !important;
}

.playeradded {
    background: #134012 !important;
}

.store--grid .store__item {
    border: 1px solid #129003!important;
}


.position-filter, .trivia-filter {
background: #2B2C2F!important;
    background-color: rgb(43, 44, 47)!important;
}



.position-filter li+li, .trivia-filter li+li {
    border-left: solid 1px #1A5613;
    border-left-width: 1px;
    border-left-style: solid;
    border-left-color: rgb(26, 86, 19)!important;
    margin-left: 0;
}

.dropdown--primary {
    background-color: #000!important;
    box-shadow: 0 0 12px #4BFF00!important;
}



#vu-contest-type-filter li.active>a, #vu-lobby-sub-header li.active>a {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#0B1001), color-stop(50%,#a1d54f), color-stop(51%,#80c217), color-stop(100%,#0C0E08)) !important;
    background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgb(11, 16, 1)), color-stop(0.5, rgb(161, 213, 79)), color-stop(0.51, rgb(128, 194, 23)), to(rgb(12, 14, 8)));
    color: #92b449;
    font-weight: 700;
}



footer {
    border-top: 1px solid #0D675A !important;
}






.jackpot-entries__entry {
    background: #000000 !important;
    background-color: rgb(0, 0, 0);
    border: 2px solid #0E4F01 !important;
    border-top-color: rgb(249, 246, 233);
}




.jackpot-countdown {
    text-align: center;
    border: 2px solid #0E4F01 !important;
    border-top-color: rgb(244, 245, 249);
    border-top-style: solid;
}

.video-column {
    background: black!important;
    border: 1px solid green!important;
}

.video-thumbnail-con {
    background: #000;
    border: 1px solid green!important;
}

.live-panel__alerts {
    border: 2px solid #108825!important;
}

.vu-trivia-matches li.current, .vu-trivia-matches li.current>a, .vu-trivia-matches li:hover {
    background: #000000!important;
    border: 1px solid green!important;
}
.live-tab__content {
    background: #000000!important;
    border: 1px solid green!important;
}
.live-tab__content .default-panel, .live-tab__content .table>thead>tr>th {
    background: #02230A!important;
}
.live-tab__content .table>tbody>tr>td {
    border-top: 2px solid #104E01!important;
}

}
