css:
@-moz-document domain("wuxiaworld.com"), domain("pactwebserial.wordpress.com"), domain("totallyinsanetranlation.com"), domain("risingdragonstranslation.com"), domain("chinanovel.net"), domain("youjinsite.com"), domain("gravitytales.com"), domain("shiroyukitranslations.com"), domain("pikatranslations.com"), domain("taffygirl13.wordpress.com") {
/* Shared between different sites */

/* TODO: Separate current styles and look into all sites for true shared. Currently just using WuxiaWorld as base */

body > div:nth-child(19), body > div:nth-child(18), body > div:nth-child(17), div#secondary {
    display: none !important;
}

#page, #content, #primary, #main, #comments, footer.site-footer, article.hentry {
    width: 100% !important;
    background-color: #111111 !important;
    font-family: "Times New Roman" !important;
    font-size: 34px !important;
    line-height: 1.2em !important;
    color: #ffffff !important;
}

a {
    color: #AAA !important;
    text-decoration: none !important;
    border-bottom: 1px solid #AAA !important;
}

a:hover {
    color: #999 !important;
}

a:visited {
    color: gray !important;
    text-decoration: none !important;
    border-bottom: 1px solid #AAA !important;
}
}

@-moz-document domain("taffygirl13.wordpress.com") {
/* Close Combat Mage */


#content, .entry-content {
    padding: 0 !important;
    margin: 0 !important;
}

.site-content {
    max-width: 100% !important;
}
}

@-moz-document domain("pikatranslations.com") {
/* Close Combat Mage */


#btn-scrollup {
    display: none !important;
}

#primary {
    padding: 0 !important;
}

.row {
    margin: 0 !important;
}

.entry-header h1, .entry-header h2 {
    color: #FFFFFF !important;
}
}

@-moz-document domain("shiroyukitranslations.com") {
/* Long Live Summons */

#page:before {
    width: 100% !important;
    background-image: none !important;
}

#header, #main, #footer {
    background-image: none !important;
}

nav#main-nav ul ul li {
    background-color: #111111 !important;
    color: #ffffff !important;
}

#main {
    padding: 0 !important;
}

.ribbon-left, .ribbon-right {
    display: none !important;
}
}

@-moz-document domain("gravitytales.com") {
/* The King's Avatar */

#main-content {
    width: 100% !important;
}

.entry-headline-wrapper, .entry-content {
    background: #111111 !important;
}

.entry-headline {
    color: #ffffff !important;
}

p {
    font-size: 34px !important;
}
}

@-moz-document domain("youjinsite.com") {
/* God and Devil World translations 151+ */

.content-area, #content, .hentry {
    margin: 0 !important;
}

.hentry {
    border: 0 !important;
    background: #111111 !important;
    padding: 0 !important;
}

.entry-content {
    background: #111111 !important;
}
}

@-moz-document domain("chinanovel.net") {
/* God and Devil World translations 84-150 */

#main {
    margin: 0 !important;
}

.current_page_ancestor, .menu-primary, .menu-item-has-children ul, .menu-unset .page_item_has_children ul, .menu-unset > ul li:hover > a, .entry {
    background: #111111 !important;
}

.entry {
    border: 0 !important;
}

.post-padding-container {
    padding: 0 !important;
}

.max-width {
    max-width: 100% !important;
}

h1, h2, h3, h4, h5, h6 {
    color: #ffffff !important;
}
}

@-moz-document domain("risingdragonstranslation.com") {
/* God and Devil World translations 1-83 */

#wrapper {
    background: #111111 !important;
    width: 100% !important;
    font-family: "Times New Roman" !important;
    padding: 0 !important;
}

#content {
    margin: 0 !important;
}
}

@-moz-document domain("totallyinsanetranlation.com") {
/* totallyinsanetranlation */

body {
    line-height: 1.75 !important;
}

li, div.post-title.box, .post-meta, p {
    line-height: inherit !important;
}


#masthead > div.row-fluid.bluth-navigation.fixed.shrunk, #masthead > div.row-fluid.bluth-navigation.fixed, #masthead > div.row-fluid.bluth-navigation, .box, .entry-content p, .entry-content ul li, .entry-content p, .entry-content ol li  {
    background-color: #111111 !important;
}

p {
    color: #FFFFFF !important;
}

.page #content article.type-page, .commentlist article.comment, .entry-container, p{
    background-color: #111111 !important;
    font-family: "Times New Roman" !important;
    font-size: 1.2em !important;
    color: #ffffff !important;
}
}

@-moz-document domain("pactwebserial.wordpress.com") {
/* Pact */

.site, .sidebar .entry-header, .sidebar .entry-content, .sidebar .entry-summary, .sidebar .entry-meta {
    max-width: 100% !important;
}

.site {
    border: none !important;
}

.site-main .sidebar-container, #bit {
    display: none !important;
}

.sidebar .entry-header, .sidebar .entry-content, .sidebar .entry-summary, .sidebar .entry-meta {
    padding: 0 15px 0 15px !important;
}
}
