css:
@-moz-document domain("juqster.com") {

/*version*/
.navbar-brand:hover::after {
  content:'Juqqsy v1.5.1';
}


/*nav resize*/
.navbar-brand:hover::after {
  font-size: 11px;
  position: relative;
  top: -5px;
  left: 5px;
  background: rgba(0,0,20,.2);
  padding: 2px 5px;
  border-radius: 2px;
}

.navbar {
  min-height: 25px;
  margin-bottom: 5px;
  border: 0;
  opacity: .3;
  transition: .15s;
  z-index: 1000;
}

.navbar:hover, 
.navbar:focus, 
.navbar:active {
  opacity: 1;
}

.navbar-default {
  background: linear-gradient(#3498db,#2181C0);
  border-color: transparent;
  border-radius: 0;
  border-bottom: 1px solid #A2CFF0;
}

@media (min-width: 1200px)
{
.container {
  width: 100%;
}
}

@media (min-width: 768px)
{
.navbar>.container .navbar-brand, .navbar>.container-fluid .navbar-brand {
  margin-left: 0;
}
}

.navbar-brand {
  height: auto;
  padding: 2px 0 0 0;
  font-size: 15px;
  line-height: normal;
}

.navbar-default .navbar-brand {
  padding-bottom: 0;
  margin-top: 0;
}

.navbar-brand img {
  height: 32px;
  width: 75px;
}

.navbar-default .navbar-right > li > a {
  font-size: 16px;
  padding-top: 9px;
}

@media (min-width: 768px)
{
.navbar-nav>li>a {
  padding-top: 0;
  padding-bottom: 0;
}
}

.navbar-default .navbar-right > li img.userProfile {
  max-height: 25px;
  margin-top: -6px;
}

.share-button.navbar-right.nav.navbar-btn.sharer-0 {
  height: 20px;
}

.share-button.navbar-right.nav.navbar-btn.sharer-0 label {
  font-size: 16px;
  padding: 0 10px;
  border-radius: 0;
  transition: .15s;
}

body .navbar-default .share-button label span {
  font-size: 16px !important;
}


/*fix page height*/
@media (min-width: 1900px)
{
.Override.container {
  height: 95%;
}
}

@media (min-width: 1200px) and (max-width: 1899px)
{
.Override.container {
  height: 93%;
}
}


/*bigger chatbox*/
body hubinfo .hub-info {
  border: 0;
  background: #676A6F;
}

@media (min-height: 900px)
{
.hub-info {
    min-height: 0;
}
}

.hub-info .hub-title {
  font-size: 20px;
  font-weight: 600;
  padding: 5px 10px;
  background: rgba(0,0,20,.7);
  color: #fff;
  border-radius: 0 10px 10px 0;
  line-height: normal;
  display: block;
}

.hub-info .hub-tags {
  padding-left: 10px;
}

.hub-info .hub-tags .badge {
  font-size: 11px;
  background: #406B9E;
  padding: 5px 10px;
  border-radius: 0;
}

.hub-info .hub-description {
  margin-top: 5px;
  background: rgba(0,0,20,.7);
}

.hub-info .col-xlg-9 {
  width: auto;
  display: block;
  max-height: 55px;
  overflow-y: auto;
  color: #DBECF9;
  padding: 5px 10px;
  font-size: 12px;
}

.hub-info .col-xlg-9 a {
  color: #A4D1F7;
  transition: .15s;
}

.hub-info .col-xlg-9 a:hover {
  color: #71B1F9;
  text-decoration: none;
}


/*chat*/
body chat {
  font-family: "Segoe UI", "Tahoma", "Trebuchet MS", Arial;
  font-size: 13px;
  line-height: 14px;
}

.chat li {
  margin-bottom: 2px;
  margin-left: 7px;
  margin-right: 7px;
  border-bottom: 0;
  background: linear-gradient(rgba(230,230,255,.09),rgba(230,230,255,.06));
  padding: 5px;
}

body chat .comments-container .chat .relative-time, 
body chat .comments-container .chat .song-label {
  margin-top: -3px;
  font-size: 10px;
  margin-right: 0px;
}

.chat li .song-label, 
.chat li .relative-time {
  line-height: 10px;
}

.chat li:hover .song-label, 
.chat li:active .song-label {
  max-height: 15px;
  overflow: visible;
}

.chat li .chat-img {
  margin-right: 5px;
}

.chat li .chat-img img {
  height: 32px;
  width: 32px;
}

.chat li .chat-body {
  margin-left: 37px;
}

.chat li .chat-body .header {
  margin-bottom: 2px;
}

.chat li .chat-body .header .primary-font {
  font-size: 13px;
  color: #A4D1F7;
  font-weight: 600;
}


/*image border*/
.img-circle {
  border-radius: 0px;
}


/*player/participants scrollbar*/
@media (min-width: 768px)
{
#jukebox #jukeboxContainer .player-and-playlist-container, 
#participants {
  overflow-y: scroll;
}
}


/*participants*/
participants {
  font-size: 13px;
}

.participants-container {
  padding-bottom: 40px;
}

.participants-container img {
  width: 32px;
  height: 32px;
  transition: .15s;
  float: none;
  margin-left: 0;
}


.participants-container .participant-picture-container {
  width: auto;
  padding-left: 0;
}

.participants-container .col-md-6.col-xs-7 {
  width: auto;
  padding-left: 10px;
}

.participants-container .row:hover img {
  width: 96px;
  height: 96px;
}

.participants-container .row {
  background: rgba(200,200,255,.06);
  padding: 0;
  cursor: pointer;
  margin-top: 2px;
  margin-left: 5px;
  border-right: 5px solid rgba(0,0,0,0);
}


/*player*/
body jukebox .player-and-playlist-container playlist .playlistContainer .item-odd {
  background: none !important;
}

playlist .row.item {
  border-bottom: 1px dotted rgba(220,220,255,.2);
  padding-bottom: 5px;
  margin-top: 5px;
}

.currently-playing-song .song-img-container>div {
  border-radius: 0;
}

playlist .row.item .left-panel {
  width: 13%;
}

playlist .playlistContainer .row.item {
  display: block;
}

playlist .right-panel {
  width: 85%;
  padding: 0;
  float: right;
  margin-right: 10px;
}

playlist .right-panel .row {
  margin: 0;
}

playlist .right-panel .row .col-md-12 {
  padding: 0;
}

playlist .right-panel .row:nth-child(1) {
  display: table;
  padding-top: 5px;
  width: 50%;
}

playlist .right-panel .row:nth-child(2) {
  position: absolute;
  right: 0;
  top: 5px;
}

playlist .right-panel .row:nth-child(3) {
  display: table;
}

playlist .col-md-3.col-xs-4 {
  width: auto;
  display: inline-block;
  padding: 5px 5px 0 0;
}

playlist .item a.link-to-song {
  margin-left: 2px;
}

playlist .item .song-duration {
  font-size: 13px;
}

playlist .col-md-9.col-xs-8 {
  width: auto;
  display: inline-block;
  padding: 0;
}

playlist .row.item .song-name {
  margin-bottom: 0;
  font-size: 15px;
  word-wrap: break-word;
}

playlist .row .col-md-12 p {
  font-size: 13px;
  line-height: normal;
}

playlist .item span {
  margin-right: 5px;
  padding: 6px 7px;
  border-radius: 0 !important;
}

.song-duration {
  font-family: "Segoe UI", "Tahoma", "Trebuchet MS", Arial;
  font-size: 18px;
}

@media (min-width: 767px)
{
body #jukebox {
  border-right: 1px dotted #204559 !important;
  border-left: 1px dotted #204559 !important;
}
}

.playlistContainer {
  border-top: 0;
}

.currently-playing-song {
  background: #1A191C;
  padding: 5px 10px;
}


/*volume always visible*/
.currently-playing-song:hover #slider, 
.currently-playing-song:active #slider, 
.currently-playing-song #slider, 
.currently-playing-song #slider {
  right: 20px;
}

.currently-playing-song:hover .volume, 
.currently-playing-song:active .volume, 
.currently-playing-song .volume, 
.currently-playing-song .volume {
  right: 110px;
}


/*login button resize*/
.navbar-default .navbar-right > li > a.join-now-btn {
  border-radius: 10px;
  padding-top: 2px;
  padding-bottom: 2px;
  margin-top: 6px;
  animation: 0s;
  transition: 0s;
}


/*hub-info outside chat area*/
body hubinfo .hub-info {
  position: absolute;
  top: 0;
  left: 5px;
  max-height: 90px;
  padding-bottom: 0;
  margin: 0;
}

@media (min-width: 768px)
{
body hubinfo .hub-info {
  width: 66%;
}
}

@media (min-width: 992px)
{
body hubinfo .hub-info {
  width: 66.2%;
}
}

@media (min-width: 1600px)
{
body hubinfo .hub-info {
  width: 61.7%;
}
}

body .hub-info .expand-arrow-container {
  display: none;
}

.hub-info .header-container span:nth-child(2) {
  background: rgba(0,0,20,.7);
  border-radius: 5px 0 0 5px;
  padding: 7px 11px;
  margin: 0;
  line-height: normal;
}

.hub-info .favorite-hub {
  font-size: 15px;
  padding-top: 0;
  padding-right: 0;
  margin-bottom: 0;
  cursor: pointer;
}

body hubinfo .hub-info div:nth-child(2) {
  position: absolute;
  top: 0;
  right: 75px;
}

body hubinfo .hub-info div:nth-child(2) .row {
  text-align: right;
  margin: 0;
}

body hubinfo .row .hub-tags  {
  padding: 0;
  width: auto;
}

body #participants, 
body #jukebox {
  margin-top: 90px;
  height: 84%;
}

@media (min-height: 820px)
{
body #participants, 
body #jukebox {
  margin-top: 90px;
  height: 90%;
}
}


/*participants login notification*/
@-webkit-keyframes login {
  from {
  border-right: 5px solid #2282C2;
}
  to {
  border-right: 5px solid rgba(0,0,0,0);
}
}

@-moz-keyframes login {
  from {
  border-right: 5px solid #2282C2;
}
  to {
  border-right: 5px solid rgba(0,0,0,0);
}
}

@keyframes login {
  from {
  border-right: 5px solid #2282C2;
}
  to {
  border-right: 5px solid rgba(0,0,0,0);
}
}


/*searchbox popup*/
.ui-menu .ui-menu-item {
  padding: 0 5px;
}

li.ui-menu-item {
  border: 0;
  background: linear-gradient(rgba(0,0,20,.25),rgba(0,0,0,0));
}

.ui-menu .ui-menu-item .col-md-5 {
  width: 20%;
}

.ui-menu .ui-menu-item .col-md-7 {
  width: 80%;
  font-size: 16px;
  padding-top: 3px;
}

.ui-menu .ui-menu-item .left-overlay {
  width: 17% !important;
}

.ui-menu li.ui-menu-item .song-uploader-name {
  font-size: 13px;
  font-style: normal;
  margin: 0;
  padding: 0;
}

.ui-menu li.ui-menu-item label {
  font-style: normal;
  font-size: 15px;
  margin-top: 3px;
}

.ui-menu li.ui-menu-item .fa {
  font-size: 28px;
  margin: 0;
  padding: 0;
  text-align: right;
}

li.ui-menu-item .overlay {
  background: rgba(0,0,20,0.2);
  transition: .15s;
  border: 0;
}

li.ui-menu-item .overlay div, 
li.ui-menu-item .overlay div {
  font-size: 18px;
  transition: .15s;
}


li.ui-menu-item .overlay div:hover, 
li.ui-menu-item .overlay div:active {
  font-size: 26px;
  transition: .15s;
}


/*forms*/
body chat .chat-form-container input {
  color: #cac5c5;
  background: rgba(220,220,240,.1);
  border: 0;
}

.form-control {
  padding: 6px 12px;
  font-size: 15px !important;
  line-height: normal !important;
  color: #888;
  border: 0;
  border-radius: 0;
  -webkit-box-shadow: none;
  box-shadow: none;
  transition: .15s;
}

.form-control:focus {
  -webkit-box-shadow: none;
  box-shadow: none;
  background: rgba(220,220,240,.15);
}

.form-control::-moz-placeholder {
  color: #888;
  opacity: 1;
}

.form-control:-ms-input-placeholder {
  color: #888;
}

.form-control::-webkit-input-placeholder {
    color: #888;
}

.search-container #autocomplete {
  padding: 6px 12px;
  font-size: 17px !important;
  line-height: normal !important;
  border: 0;
  border-radius: 0;
  -webkit-box-shadow: none;
  box-shadow: none;
  transition: .15s;
}

.search-container #autocomplete:focus {
  -moz-box-shadow: none !important;
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
  background: rgba(220,220,240,.15) !important;
}

.chat-form-container .btn, 
.input-group-addon {
  font-size: 13px;
  font-weight: 400;
  line-height: normal;
  border: 0;
  border-radius: 0;
  padding: 7px 12px;
  transition: .15s;
}




