css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aretegaming.com") {

    body#phpbb {
        background-image: url("http://static1.squarespace.com/static/5411df7ee4b01dce1367679d/t/559e9f96e4b0039626f80148/1436458902191/MagnatronFinal3+smaller.jpg");
    }
    body#phpbb {
        font-family: Helvetica Neue
    }
    body#phpbb {
        font-size: 11px
    }
    div.mainheader {
        background-image: none
    }
    div.mainheader {
        background-color: #171717
    }
    .mainnav {
        font-family: Helvetica Neue
    }
    div.topnav {
        background-image: none
    }
    div.topnav {
        background-color: #0D0D0D
    }
    div.topnav {
        border-top: none
    }
    div.topnav {
        border-bottom: none
    }
    div.topnav {
        text-transform: none
    }
    div.nav-bar {
        display: none
    }
    div.mainnav {
        background-color: #0D0D0D
    }
    div.mainheader {
        margin-bottom: 0px
    }
    div.mainheader {
        border: none
    }
    div.mainheader {
        border-radius: 0px
    }
    div.mainheader {
        box-shadow: none
    }
    div#wrap {
        padding: 0px
    }
    div.forabg {
        background-image: none
    }
    div.forabg {
        background-color: #ffffff
    }
    div.inner {
        border-width: 1px
    }
    div.inner {
        border-color: #858585
    }
    ul.topiclist li.header dl.icon {
        display: none
    }
    div.mChatPanel {
        background-color: #171717
    }
    div.mChatPanel div {
        display: none
    }
    div.mChatStats {
        display: none
    }
    input.button2 {
        background-image: none
    }
    input.button2 {
        background-color: #171717
    }
    input.button2 {
        border-color: #171717
    }
    input.button2 {
        border-radius: 0px
    }
    input.button2 {
        font-family: Helvetica Neue
    }
    input.button2:hover {
        background: #171717
    }
    input.button2:hover {
        border-color: #FF1A7F
    }
    select {
        border-radius: 0px
    }
    select {
        padding: 2px
    }
    select {
        font-family: Helvetica Neue
    }
    a.dualcore-logo {
        background-image: url("http://i.imgur.com/QwnPeTM.png")
    }
    div.mainfooter {
        background: #171717
    }
    div.mainfooter {
        border-radius: 0px
    }
    div.mainfooter {
        border-color: #ffffff
    }
    input#keywords {
        border-radius: 0px
    }
    input#keywords {
        right: 0px
    }
    input#keywords {
        font-family: Helvetica Neue
    }
    input.mChatText {
        font-family: Helvetica Neue
    }
    input.mChatText:hover {
        color: #0D0D0D
    }
    input.mChatText {
        color: #0D0D0D
    }
    div.copyright {
        display: none
    }
    .portal-navigation {
        display: none
    }
    li.header dl dt img {
        display: none
    }
    li.header dt {
        color: #0D0D0D
    }
    li.header dt {
        font-family: Helvetica Neue
    }
    #portal-body {
        padding-top: 0px
    }
    #page-body {
        margin: 0px 0px
    }
    div.inner span.corners-top {
        display: none
    }
    tbody tr td {
        height: 120px
    }
    li.row {
        border-top: 1px solid #858585
    }
    li.row {
        background-color: #ffffff
    }
    li.row {
        box-shadow: none
    }
    div.panel.bg1 {
        box-shadow: none
    }
    div.panel.bg1 {
        background-color: #ffffff
    }
    div.content {
        font-family: Helvetica Neue
    }
    div.content {
        font-size: 1em
    }
    span.portal-corners-top-inner {
        display: none
    }
    a.topictitle {
        color: #171717
    }
    a.topictitle {
        font-family: Helvetica Neue
    }
    a.topictitle {
        letter-spacing: 1px
    }
    a:link {
        color: #171717
    }
    a:link {
        font-family: Helvetica Neue
    }
    ul.topiclist li {
        color: #858585
    }
    ul.topiclist {
        color: #858585
    }
    ul.topiclist.bg1 {
        background-color: #ffffff
    }
    ul.topiclist.bg1 {
        box-shadow: none
    }
    div.maintitle {
        background: none
    }
    div.maintitle {
        background: #171717
    }
    div.maintitle {
        border: 1px solid #171717
    }
    div.maintitle {
        border-radius: 0px
    }
    div.maintitle {
        box-shadow: none
    }
    div.maintitle {
        font-family: Helvetica Neue
    }
    div.maintitle {
        font-size: 18px
    }
    ul.linklist li {
        font-size: 1.3em
    }
    .forumbg {
        background-color: #ffffff
    }
    .forumbg {
        background-image: none
    }
    .pagination span strong {
        background-color: #858585
    }
    .pagination span strong {
        border-color: #FF1A7F
    }
    a.forumtitle {
        color: #FF1A7F 
    }
    body#phpbb.section-viewtopic.ltr {
        color: #FF1A7F 
    }
    dd.topics span, dd.posts span, dd.views span {
        color: #0093CA
    }
    div.prog_green {
        background: #008040 url("http://aretegaming.com/images/donation/prog_green.png") repeat-x scroll 0% 0%
    }
    div.panel {
        background-color: #ffffff
    }
    div.panel {
        box-shadow: none
    }
    div.panel {
        color: #0D0D0D
    }
    div.panel {
        font-family: Helvetica Neue
    }
    h2 {
        color: #FF1A7F 
    }
    div.prog_grey {
        background: #CCC url("http://aretegaming.com/images/donation/prog_grey.png") repeat-x scroll 0% 0%
    }
    tr.bg1 {
        box-shadow: none
    }
    tr.bg1 {
        background-color: #ffffff
    }
    tr.bg2 {
        box-shadow: none
    }
    tr.bg2 {
        background-color: #ffffff
    }
    input.button1 {
        background-image: none
    }
    input.button1 {
        background-color: #171717
    }
    input.button1 {
        border-color: #171717
    }
    input.button1 {
        border-radius: 0px
    }
    input.button1 {
        font-family: Helvetica Neue
    }
    input.button1:hover {
        background: #171717
    }
    input.button1:hover {
        border-color: #FF1A7F
    }
    #navigation a {
        background-image: none
    }
    #navigation a {
        background-color: #0093CA
    }
    #cp-main .panel {
        background-color: #ffffff
    }
    #cp-main .panel {
        box-shadow: none
    }
    .cp-mini {
        background-color: #ffffff
    }
    div#page-body {
        font-family: Helvetica Neue
    }
    h3 {
        color: #FF1A7F
    }
    #tabs a span {
        background: #0093CA
    }
    #tabs a span {
        background-image: none
    }
    #tabs a  {
        background: #0093CA
    }
    #tabs a  {
        font-family: Helvetica Neue
    }
    #tabs .activetab a  {
        border-bottom-color: #FF1A7F
    }
    .newpm-icon span {
        background-image: url("http://i.imgur.com/ir8aQDr.png")
    }
    .vt-bg1 {
        background-color: #ffffff
    }
    .vt-bg1 {
        box-shadow: none
    }
    .vt-bg2 {
        background-color: #ffffff
    }
    .vt-bg2 {
        box-shadow: none
    }
    .reply-icon span {
        background-image: url("http://i.imgur.com/NHGIEG0.png")
    }
    .quote-icon, .quote-icon a {
        background-image: url("http://i.imgur.com/5ioItL2.png")
    }
    .report-icon, .report-icon a {
        background-image: url("http://i.imgur.com/ojo2lOM.png")
    }
    .edit-icon, .edit-icon a {
        background-image: url("http://i.imgur.com/fyt2eQi.png")
    }
    .pm-icon, .pm-icon a {
        background-image: url("http://i.imgur.com/AsdTzkm.png")
    }
    .web-icon, .web-icon a {
        background-image: url("http://i.imgur.com/N73OSc6.png")
    }
}
