css:
@-moz-document domain("mashreghnews.ir"), domain("mashreghnews.com") {
  
  /*
   *
   *
   *author : Ali mousavi (mousavi.ali66@gmail.com)
   *
   *
   */
 
 /* asign the menu to the top */
.scroller{top : 0 !important;opacity : 0.88;filter : alpha(opacity=88);}
  
 /*hide all images or flash object that have "adv" keyword */   
img[src*="adv"], object[data*="adv"]{display : none!important;}
  
/*make image transparent when loading using animation*/
img.img-responsive {
    border-radius : 5px;
  	-moz-border-radius : 5px;
  	-webkit-border-radius : 5px;
  
  	/*transition: all 4s ease-in-out;
  	-webkit-transition: all 0.4s ease-in-out;
  	-o-transition: all 0.4s ease-in-out;
  	-moz-transition: all 0.4s ease-in-out;
  
    transition-delay : 1s;
    transform: scale(0.98);
    -ms-transform: scale(0.98);
    -moz-transform: scale(0.98);
    -webkit-transform: scale(0.98);
  	
  	opacity : 0.5;
  	filter : alpha(opacity=50);*/
}
  
/*set transparency to 100 when hover on image*/ 
img.img-responsive:hover{
    /*transition: all 2s ease-in-out;
  	-webkit-transition: all 0.4s ease-in-out;
  	-o-transition: all 0.4s ease-in-out;
  	-moz-transition: all 0.4s ease-in-out;
  
    transform: scale(1);
    -ms-transform: scale(0.98);
    -moz-transform: scale(0.98);
    -webkit-transform: scale(0.98);
  	
  	opacity : 1.0;
  	filter : alpha(opacity=100);*/
}
 
 /*change color based on user choice*/ 
.r_sec_sar, .service_div, .nav_service_cats, .r_sec_sar2, .cat_div, 
.footer, .comments_topic_b, .comments_topic_b2, .link_cat, .item_List_Date, .en-logo>span, 
.owl-theme .my_dots .owl-dot.active span, .owl-theme .my_dots .owl-dot:hover span, 
.owl-theme .my_dots2 .owl-dot.active span, .owl-theme .my_dots2 .owl-dot:hover span, 
div[class*="odd_prayer"]{
  background-color : /*[[bg_color]]*/ !important; 
}

#news .form-control:focus{
    -webkit-box-shadow: inset 0 1px 1px /*[[bg_color]]*/, 0 0 8px /*[[bg_color]]*/!important;
    -moz-box-shadow: inset 0 1px 1px /*[[bg_color]]*/, 0 0 8px /*[[bg_color]]*/!important;
    box-shadow: inset 0 1px 1px /*[[bg_color]]*/, 0 0 8px /*[[bg_color]]*/!important;
    opacity : 0.6!important;
    filter : alpha(opacity=60)!important;
}
  

.nav_service_cats, .cat_div, .item_List_Date, div[class*="odd_prayer"]{ opacity : 0.8!important; filter : alpha(opacity=90)!important;}

/* make two edge coloumn transparent to keep focus on news content */
#news .col-xs-12 .col-ms-12 .col-sm-4 .col-md-3>.row, #news .col-md-3{
    opacity : 0.1!important;
    filter : alpha(opacity=10)!important;
    transition : opacity 0.8s ease-in-out;
    -webkit-transition : opacity 0.8s ease-in-out;
  	-moz-transition : opacity 0.8s ease-in-out;
  	-o-transition : opacity 0.8s ease-in-out;
    transition-delay : 1s;
}

/*make two transparented edges column opaque*/
#news .col-xs-12 .col-ms-12 .col-sm-4 .col-md-3>.row:hover, #news .col-md-3:hover{
  opacity : 1!important;
  filter : alpha(opacity=100)!important;
  -webkit-transition : opacity 0.8s ease-in-out;
  -moz-transition : opacity 0.8s ease-in-out;
  -o-transition : opacity 0.8s ease-in-out;
  transition : opacity 0.8s ease-in-out;
} 

}
