css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("inoreader.com") {
  body {
    font-family:'Arial','Hiragino Sans GB','Microsoft YaHei','WenQuanYi Micro Hei','sans-serif'!important;

  }

  /* 将背景色改为土黄土黄的 :p  */
  .article_card, .article_title, .article_sub_title, .article_footer, .article_full_contents,.article_full_contents  {
    background-color: #F6F4EC!important;
  }

  /* 调整文章详情区域的宽度、字体、字号、行距 */
  .article_content {
    font-size:16px !important;
    line-height:200%;
    background-color: #F6F4EC!important;
    width:98% !important;
    max-width:inherit!important;
    font-family: 'Open Sans','Helvetica Neue',Arial,'Hiragino Sans GB','Microsoft YaHei','WenQuanYi Micro Hei',sans-serif!important;
  }

  /* 
   处理部分图片因为 float:left 导致把文字压在右边的问题，
   现在文本内容（p 标签）会在图片下另起一行
  */
  .article_content p {
    clear: left;                       
  }

  .article_title_link {
    font-weight: bold !important; 
    font-size:20px !important;
  }

  .article_tags_inner {
    background-color: #F6F4EC!important;
  }

  .article_tags {
    color: #9c9c9c;
  }

  #three_way_contents {
    background-color: #F6F4EC !important;
  }

  #reader_pane {
    background-color: #F6F4EC !important;
  }


  /* 隐藏边框 */
   div.article_tile,div.article_card.article_current,.article_card.ar,.bundle_wrap,.discover_feed {
    border: none;
  }

  .article_expanded {
    background-color: #F6F4EC !important;
  }

  .article_tile_title {
    padding: 0px 2px;
  }

  .article_content img, .article_content iframe, .article_content div{
    max-width: 100% !important;
  }

  /* 控制宽度 */
  .reader_pane_view_style_2{
  	width:400px!important;
  }

  #three_way_contents{
    width:calc(100% - 740px)!important;
   left:670px!important;
  }

  .article_footer_3way{
    width:calc(100% - 740px) !important;
  }

  /* 隐藏广告 */
  /* 隐藏广告 */
  .block_article_ad, .ad_title, .avs_iframe, .img_ad, .inner_ad, .ad_footer_remove, #sinner_container {
    display: none !important;
  }

  .reader_pane_sinner {
    padding-right: 0  !important;
  }
}
