@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('wmmail.ru')
{
    .tm2
    {
        position: fixed;
        z-index: 100;

        display: block;

        height: 60px;
        margin-top: 0;
        padding-left: 150px;

        border-bottom: 2px solid #4C9ED9;
        background: white;
    }

    .tm2 > tbody > tr
    {
        position: fixed;

        margin-top: 3px;
        margin-left: 25px;
    }

    .bot_left
    {
        padding: 0 0;

        background: white;
    }

    .colum + td
    {
        padding-top: 60px;
        padding-left: 20px;
    }

    .colum
    {
        padding: 0 8px;
    }

    .colum_right > .rblue ~ .rblue
    {
        display: block;
       
    }

    .mb
    {
        position: fixed;
        z-index: 99999;

        height: 1000px;
        margin-top: 0;

        background: #4c9ed9;
    }

    .mb > tbody > tr
    {
        display: block;

        margin-left: 50px;
    }

    form[id='login']
    {
        margin-left: 30%;
    }

    .bot_left > table ~ table
    {
        display: block;
    }

    .top_logo_fon
    {
        background: white;
    }

    .by
    {
        float: right;
        clear: both;

        margin: 0 0 10px 10px;

        border-top: 6px solid #ffcc4d;
        border-radius: 0 0 15px 15px;
        background: #63c7f8;
    }

    a[href='index.php?cf=akk-changeavatar'] > img
    {
        position: fixed !important; z-index: 102; top: 1px; left: 12px;width: 55px; height: 55px; 
    }


    .myinfo
    {
        position: fixed !important; z-index: 102; top: 1px; left: 80px;
    }

    a[href='index.php?cf=wm-wmpaymwent']
    {
        position: absolute; z-index: 99; top: -29px;  left: 100px;
    }
    a[href='index.php?cf=wm-ftranscreate']
    {
        position: absolute; z-index: 99; top: -27px; left: 0;  border-right: 104px solid #fbeea6;border-left: 16px solid #fbeea6; 
    }

    .reg > div > table
    {
        position: absolute;
    }


    .reg > div > table ~ table
    {
        position: relative;
        border-bottom:  1px solid #dc895d;
        margin-top: 87px;
    }

    img[src='img/x.gif']
    {
        position: fixed !important; z-index: 101; top: 0; left: 7px; width: 180px; height: 59px; background: #4c9ed9;
    }

    .bmenu
    {
        position: relative;
        margin-top: -18px;
    }

    .bmenu_bot
    {
        padding-bottom: 20px;
    }

    .bmenu_top
    {
        font-size: 12px;
        font-weight: bold;

        width: 160px;
        height: 26px;
        margin: 0 -4px 0 -4px;
        padding: 5px 20px 3px 8px;

        text-align: left;
        text-transform: uppercase;

        color: #fff;
        border-top: 2px solid #dc895d;
        border-radius: 5px;
        background: #f5c61a;
    }

    div.bmenu_title
    {
        font-size: 12px;
        font-weight: bold;

        width: 158px;
        height: 26px;
        margin: 0 -4px 0 -4px;
        padding: 5px 20px 3px 8px;

        text-align: left;
        white-space: nowrap;
        text-transform: uppercase;

        color: #fff;
        border-radius: 3px;
        background: #f5c61a;
    }


    .rblue div.dblue_top
    {
        font-weight: bold;

        width: 157px;
        height: 20px;
        padding: 7px 10px 4px 15px;

        color: #0079c6;
        border-radius: 5px;
        background: #f5c61a;
    }

    .rblue
    {
        margin-top: 30px;
    }
    
    .bmenu + div {
        margin-top: -8px;
    }

    div.lreg:first-child
    {
        position: fixed;
        z-index: 999;
        top: 0;
        left: 0;

        border-radius: 5px;
        background: #fbeea6;
    }

    .rblue
    {
        margin-top: 30px;
    }

      
    .blue[href='index.php?cf=wm-wmpaymwent'],
    .blue[href='index.php?cf=wm-ftranscreate'],
    div.by_menu a:nth-child(3),
    .attention3,
    .rnews,
    .lblue,
    .colum_right > .rblue,
    td[width='33%'],
    .mb > tbody > tr ~ tr,
    .mb_blue,
    .mb_orng,
    .mb_right,
    .dobro,
    .tm,
