iframe#kiwi .panel_container {
  overflow-y: auto;
  height: 100%;
  background: #000;
}
iframe#kiwi .toolbar .topic div {
  padding: 0.2em 1em;
  text-align: center;
  box-shadow: none;
  border-radius: 0;
  background-color: #000;
  height: 1.5em;
  overflow: hidden;
  outline: none;
  white-space: nowrap;
}
iframe#kiwi .controlbox .input {
  background: #555;
  margin: 3px;
  height: 1.7em;
  border-radius: 5px;
  -moz-border-radius: 5px;
  -webkit-border-radius: 5px;
  -khtml-border-radius: 5px;
}
iframe#kiwi .controlbox .input .inp {
  line-height: 1.7em;
  border: medium none;
  box-shadow: none;
  border-radius: 0;
  outline: none;
  resize: none;
  overflow: hidden;
  position: relative;
  height: 100%;
  width: 100%;
  display: block;
  background: #555;
}
iframe#kiwi .controlbox .input .nick {
  text-align: right;
  width: 11em;
  left: 0px;
  position: absolute;
  overflow: hidden;
  background-color: #000;
  border-radius: 3px 0 0 3px;
  border-right: 1px solid #ccc;
  padding: 2px;
  padding-right: 0.5em;
}
iframe#kiwi .right-bar-content {
  position: relative;
  height: 100%;
  z-index: 0;
  background: #000;

}
iframe#kiwi .memberlists ul li a.nick {
  display: block;
  color: #555;
}


