css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
 @font-face {
	font-family:'Roboto';
	font-style:normal;
	font-weight:400;
	src:local('Roboto Regular'),local('Roboto-Regular'),url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2)format('woff2');
}
body, * {
font-family: "Roboto" !important;
 font-size: 16px !important;   
transition: all 0.2s ease-in-out 0s !important;
}
  
/* Hider Code */ 
#below-install-ad, .ad, #Form_Go{
display: none!important;
}
  
/*input[type="text"],*/input#search-terms, input[type="checkbox"], input#style_short_description, input#style_pledgie_id, input[name="new_screenshot_description_5"], input[name="new_screenshot_description_4"], input[name="new_screenshot_description_3"], input[name="new_screenshot_description_2"], input[name="new_screenshot_description_1"], input[placeholder="Label"], input[placeholder="Install Key"], input[type="url"],input[type="button"],textarea,/*input,*/ select{
-moz-appearance: none!important;
background: rgba(0,0,0,0.2)!important;
border: 1px solid rgba(0,0,0,0.2)!important;
border-radius: 3px!important;
box-shadow: 0 0 0 1px rgba(255,255,255,0.05)!important;
color: #333!important;
padding: 5px!important;
margin: 1px!important;
}
  
input[type="button"],input[type="submit"],input[type="file"],button, input#search-submit {
color: #333!important;
border-radius: 3px!important;
padding: 4px!important;
background:linear-gradient(#878787, #5D5D5D)!important;
border: 1px solid rgba(255,255,255,0.08)!important;
box-shadow: 0 0 0 1px rgba(0,0,0,0.2)!important;
}
  
#Body{
padding-top: 15px!important;
}
  
#control-panel{
float: left!important;
}
  
input[type="button"]:hover,input[type="submit"]:hover,input[type="submit"]:hover{
background:linear-gradient(#9E9E9E, #898989)!important;
}

#discussions-area, #additional-info{
float: left!important;
clear: left!important;
}

.edit-setting{
background: none!important;
}
  
.edit-style-options > *{
margin: 5px!important;
}

#info-tables {
clear:right!important;
float:left!important;
width:390px!important;
font-size: 11px!important;
}

#long-description ~ *{
width: 360px!important;
}

#info-tables>table {
margin:0 0 0 7px!important;
}
  
.Count{
background: rgba(0,0,0,0.2)!important;
}
  

.lb-outerContainer{
background: none!important;
}

#login-options a {
background-image:url(data:image/png;base64,)!important;
border-radius:3px!important;
color:rgba(255,255,255,0.7)!important;
font-weight:normal!important;
line-height:15px!important;
outline:none!important;
padding:5px 10px 0 25px!important;
}

#main-article {
margin-left:0!important;
margin-right:200px!important;
padding:15px 25px 0 25px!important;
}

#main-article p a[href*="/styles/browse/"] {
background:url("http://i.imgur.com/yXjoN17.png")no-repeat!important;
display:inline-block!important;
height:15px!important;
line-height:13px!important;
margin-top:2px!important;
}
  
#main-article p a[href="/styles/browse/global"]{
background: none!important;
}

#main-article p a[href="/styles/browse/app"] {
background-position:0 -45px!important;
padding-left:18px!important;
}

#main-article p a[href="/styles/browse/facebook"] {
background-position:0 0!important;
padding-left:18px!important;
}

#main-article p a[href="/styles/browse/google"] {
background-position:0 -30px!important;
padding-left:18px!important;
}

#main-article p a[href="/styles/browse/tumblr"] {
background-position:0 -15px!important;
padding-left:18px!important;
}

#main-header, #Head {
background:rgba(33,37,43,0.4)!important;
border-bottom:1px solid rgba(0,0,0,0.1)!important;
border-radius:0!important;
box-shadow: 0 1px 0 rgba(255,255,255,0.04)!important;
color:rgba(255,255,255,0.7)!important;
height:60px!important;
line-height:30px!important;
margin:0!important;
padding:0 0 0 20px!important;
}
  
#Head{
line-height: 60px!important;
}

#main-header h1{
font-size:14px!important;  
position: absolute!important;
}

#main-header h1 a {
background:url('http://i.imgur.com/dVYgtqX.png') no-repeat!important;
display:inline-block;
height:32px!important;
margin-top:15px!important;
padding-left:25px!important;
text-indent:-10000px!important;
}

#main-style-info{
margin-left: 0!important;
}

#more-screenshots {
padding:10px 5px!important;
}

#related {
background:none!important;
clear:both!important;
float:left!important;
margin:-38px 0 0 0!important;
padding:0!important;
}

#related h2 {
background:#24272c!important;
border-radius:3px!important;
color:#888!important;
padding:7px 10px!important;
}

#related li {
padding:5px 0 2px 5px;
}

#screenshots {
border:none!important;
}

#search-submit {
background: white!important;
border:none!important;
border-radius:0 4px 4px 0!important;
color: #777!important;
float:none!important;
height:30px!important;
margin:0!important;
width:22px!important;
}

#search-terms {
background:white!important;
border:none!important;
border-radius:4px 0 0 4px!important;
height:30px!important;
margin:0 0 0 0!important;
padding:0 0 0 5px!important;
width:173px!important;
-moz-appearance:none!important;
}
  
#style-info{
float: left!important;
max-width: 100%!important;
}

#view-code, pre {
background: rgba(0,0,0,0.2)!important;
border-radius: 4px!important;
border: 1px solid rgba(0,0,0,0.2)!important;
float:none!important;
clear: both!important;
width: -moz-available!important;
width:-webkit-fill-available!important;
white-space: pre-wrap!important;
}

#view-code{
height: 360px!important;
}

.advanced-search-active #search-terms {
border-radius:4px!important;
width:195px!important;
}

.btn-auth:before {
display:none!important;
}

.btn-google {
background:#2f5080 5px 0 no-repeat!important;
border:1px solid #3a63a2!important;
}

.btn-openid {
background:#e76822 5px -58px no-repeat!important;
border:1px solid #ff803b!important;
}

.btn-password {
background:#cd232f 5px -87px no-repeat!important;
border:1px solid #ed2634!important;
}

.btn-yahoo {
background:#553c5e 5px -29px no-repeat!important;
border:1px solid #71527c!important;
}

.screenshot-thumbnail img {
border-radius:3.5px!important;
}

a:link {
color:#5F5F60 !important;
text-decoration:none!important;
outline: none!important;
}
a:link:hover {
color:#000 !important;
}
a:visited:hover {
color:#1A1A1A !important;
}
a:visited {
color:#5F5F60 !important;
}

body {
background: none /*[[asshole]]*/ top center no-repeat!important;
color:#333 !important;
margin:0!important;
max-width:100%!important;
padding:0!important;
}

div[id*="front-page"] ul {
padding-left:25px!important;
}

footer {
margin-top:20px!important;
position:relative!important;
top:50px!important;
}

footer a[href="/contact"] {
background:url('http://i.imgur.com/dVYgtqX.png') -25px 0 no-repeat!important;
bottom:10px!important;
left:25px!important;
padding:10px 25px 10px 25px!important;
position:absolute!important;
}

#left-info{
float: none!important;
margin: 0 auto!important;
}

#left-sidebar {
background:none!important;
border-radius:4px!important;
margin:0 10px 0 0!important;
max-height:1200px!important;
min-height:260px!important;
position:absolute!important;
right:5px!important;
top:90px!important;
width:200px!important;
}

#left-sidebar nav {
background:none!important;
border-radius:0!important;
margin:0!important;
padding:0!important;
}

hr{
border: none!important;
border-top: 1px solid rgba(0,0,0,0.15)!important;
}

.PanelCategories li {
border-bottom: 1px solid rgba(0,0,0,0.1)!important;
}

.Item.Read{
background: none!important;
filter: url(data:image/svg+xml;base64,PHN2ZyB2ZXJzaW9uPSIxLjEiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+PGZpbHRlciBpZD0ic3QiPjxmZUNvbG9yTWF0cml4IHZhbHVlcz0iMC4xNCAwLjQ1IDAuMDUgMCAwIDAuMTIgMC4zOSAwLjA0IDAgMCAwLjA4IDAuMjggMC4wMyAwIDAgMCAwIDAgMSAwIiAvPjwvZmlsdGVyPjwvc3ZnPg==#st)
}

/* Sidebar Code */

#left-sidebar nav ul li:not(#sidebar-search) {
background:url("http://i.imgur.com/u9UukBg.png") no-repeat!important;
float:left!important;
line-height:15px!important;
margin-top:7px!important;
padding:10px 0 10px 38px!important;
width:162px!important;
}

#left-sidebar nav ul li#sidebar-browse {
background-position:8px 0!important;
}

#left-sidebar nav ul li#sidebar-forum {
background-position:8px -105px!important;
}

#left-sidebar nav ul li#sidebar-help {
background-position:8px -68px!important;
}

#left-sidebar nav ul li#sidebar-login {
background-position:8px -35px!important;
}

#left-sidebar nav ul li#sidebar-my-account {
background-position:8px -140px!important;
}

#left-sidebar nav ul li:hover:not(#sidebar-search) {
background-color:#24272c!important;
border-radius:3px!important;
}

#left-sidebar ul li a {
color:#888!important;
text-decoration:none!important;
}

}

@-moz-document domain("userstyles.org") {

footer:after{
content: ""!important;
}
}
