css:
 /* i really want this to be global */
@-moz-document domain("fourmizzz.fr"),
domain("antzzz.org") {
body{
   background-color:#CCB064 !important;
}
#centre{
   background-image: url('http://img15.hostingpics.net/pics/177576oiesAC6KHmSM90F.png') !important; 
   background-position: right;
   background-repeat: no-repeat;
   background-size : 50% !important;
}
#centre img {max-width: 100%; /* les images seront limitées à la largeur de leur parent */}
#gauche,#droite{position:fixed;top:0px;bottom:0px;width:250px;background-repeat: no-repeat;background-color:black;}
  #gauche{
   left:0px;
   background-position:top right;
   border-right:solid 0px white;
   background-image: url('http://www.hdwallpaper.nu/wp-content/uploads/2015/02/fire-angel-fantasy-hd-wallpaper-1920x1080-9655.jpg') !important;
   background-position: center !important;
   background-repeat: repeat !important;
   background-size : auto !important;
   top: 0px!important;
   background-color: transparent !important;
   -webkit-box-shadow: inset -5px 0px 25px 0px #000;
   -o-box-shadow: inset -5px 0px 25px 0px #000;
   box-shadow: inset -5px 0px 25px 0px #000;
}

#droite {
   display:none;
   background-image: url('http://voyageips.com/vips/wp-content/uploads/2013/09/Demonagel1.jpg') !important;
   background-position: center!important;
   background-repeat: repeat !important;
   background-size : 400% !important;
   position:fixed;
   top:0px!important;bottom:0px;right:0px;
   border-left:solid 0px white !important;
   background-color:transparent !important;
   -webkit-box-shadow: inset 5px 0px 25px 0px #000;
   -o-box-shadow: inset 5px 0px 25px 0px #000;
   box-shadow: inset 5px 0px 25px 0px #000;
}

#droite:before {
   content:"▬ illae magicae angeli ▬" !important;
   color: red !important;
   text-shadow: 1px 1px white;
   text-decoration:none;
   text-align: center!important;
   font-size:0.9em !important;
   -webkit-border-radius: 5px 5px 5px 5px !important; 
   border-radius: 5px 5px 5px 5px !important; 
   border: solid 2px  white !important;
   box-shadow: 0px 0px 5px 1px white !important;
   padding:5px;
   background-image: url('http://images.forwallpaper.com/files/images/4/4256/4256dab2/293435/mouse-maps-focus.jpg') !important;
   background-position: top center !important;
   background-repeat: repeat !important;
   margin-left: 10%;
   opacity: 0.85 !important; 
}
    
@media screen and (max-width: 61em){ /*980px*/#centre {margin:0px 5px 0px 5px;padding-top:53px;}#gauche {display:none;background-image:none;}}
@media screen and (min-width: 84em){/*1340px*/#centre{margin-right:260px;}#droite{display:block;}}
@media screen and (min-width: 97em){/*1550px*/#centre{margin:0px 310px 0px 310px;}#gauche,#droite{width:300px;}}
@media screen and (min-width: 112em){/*1800px*/#centre{margin:0px 410px 0px 410px;}#gauche,#droite{width:400px;}}
}

#menu{
box-shadow:0px 0px 2px 1px white !important;
background: black!important;
   Border : solid 3px white;
border-radius:15px !important;
background-position: top center !important;
}
#menu ul li a{
background: #111111 !important;
border-radius: 15px !important;
   Border : double 3px white;
box-shadow:0px 0px 2px 1px red !important;
}

ul.menu_colonne li {
    margin: 5px 0 5px 2% !important;   
}

.ligneTableau:nth-child(2n+1){
border:2px solid !important;
background:white !important;
}
.titre_colonne_cliquable,.boite_connexion_titre{
background:#000000 !important;
border-radius: 20px 20px 0px 0px!important;
    Border : dotted 1px white;
    box-shadow: 0px 0px 5px 1px red !important;
}
.contenu_boite_compte_plus,.contenu_boite_info,.boite_connexion_contenu,.boite_connexion_contenu, .boite_presentation_contenu{
background:rgba(40,40,40,0.9) !important;
top:35px !important;
  Border : dotted 1px white;
    box-shadow: 0px 0px 5px 1px red !important;
border-radius: 0px 0px 10px 10px!important; 
}

ul.menu_colonne {
    background: rgba(40,40,40,0.9) !important;
    box-shadow: none !important;
}
