#loadingScreen {
	background: #2B2E2F !important;
}
#loadingScreenSpinner {
	background: none !important;
	width: 80px !important;
	height: 80px !important;
	border-radius: 80px;
	border: 2px solid #667079;
	top: 28%;
	top: -webkit-calc(50% - 43px);
	top: calc(50% - 43px);
	left: 35%;
	left: -webkit-calc(50% - 43px);
	left: calc(50% - 43px);
	-webkit-transform-origin: 50% 50%;
	transform-origin: 50% 50%;
	-webkit-animation: spinner 2s linear infinite;
	animation: spinner 2s linear infinite;
	box-shadow: inset 0 0 0 10px rgba(73, 81, 87, 0.75),
							inset 0 0 0 20px rgba(73, 81, 87, 0.4),
							inset 0 0 0 30px rgba(73, 81, 87, 0.25);
}
#loadingScreenSpinner:after {
	content: "";
	position: absolute;
	top: -5px;
	left: 10px;
	width: 12px;
	height: 12px;
	border-radius: 12px;
	background-color: #27ae60;
	border: 3px solid #2b2e2f;
	-webkit-animation: orbit 1s linear infinite;
	animation: orbit 1s linear infinite;
	box-shadow: 0 0 24px 0 rgba(39, 174, 96, 0.75);
}
#loadingScreenMessage {
	display: block !important;
	margin-top: 20px !important;
	text-transform: uppercase;
	font-size: 20px !important;
	color: #667079;
	-webkit-animation: loading 2s linear infinite;
	animation: loading 2s linear infinite;
}
@-webkit-keyframes spinner {
	0% {
		-webkit-transform: rotate(0deg);
        transform: rotate(0deg);
		box-shadow: none;
		border-color: #667079;
	}
	50% {
		box-shadow: inset 0 0 0 10px rgba(73, 81, 87, 0.75),
								inset 0 0 0 20px rgba(73, 81, 87, 0.4),
								inset 0 0 0 30px rgba(73, 81, 87, 0.25);
		border-color: #a5b6c3;
	}
	100% {
		-webkit-transform: rotate(360deg);
        transform: rotate(360deg);
		box-shadow: none;
		border-color: #667079;
	}
}
@keyframes spinner {
	0% {
		-webkit-transform: rotate(0deg);
        transform: rotate(0deg);
		box-shadow: none;
		border-color: #667079;
	}
	50% {
		box-shadow: inset 0 0 0 10px rgba(73, 81, 87, 0.75),
								inset 0 0 0 20px rgba(73, 81, 87, 0.4),
								inset 0 0 0 30px rgba(73, 81, 87, 0.25);
		border-color: #a5b6c3;
	}
	100% {
		-webkit-transform: rotate(360deg);
        transform: rotate(360deg);
		box-shadow: none;
		border-color: #667079;
	}
}
@-webkit-keyframes orbit {
	0% { box-shadow: none; }
	25% { box-shadow: 0 0 24px 0 rgba(39, 174, 96, 0.75); }
	50% { box-shadow: none; }
	75% { box-shadow: 0 0 24px 0 rgba(39, 174, 96, 0.75); }
	100% { box-shadow: none; }
}
@keyframes orbit {
	0% { box-shadow: none; }
	25% { box-shadow: 0 0 24px 0 rgba(39, 174, 96, 0.75); }
	50% { box-shadow: none; }
	75% { box-shadow: 0 0 24px 0 rgba(39, 174, 96, 0.75); }
	100% { box-shadow: none; }
}
@-webkit-keyframes loading {
	0% { color: transparent; }
	50% { color: #667079; }
	100% { color: transparent; }
}
@keyframes loading {
	0% { color: transparent; }
	50% { color: #667079; }
	100% { color: transparent; }
}
