css:
@-moz-document url-prefix("https://www.fastmail.com/calendar") {
.v-OverviewEvent {
    color: #0D47A1 !important;
    margin-left: 2px !important;
    margin-right: 2px !important;
    width: auto !important;
}
.v-OverviewEvent.v-OverviewEvent--allDay.v-OverviewEvent--first {
    color: #0D47A1 !important;
    background: #E3F2FD !important;
}
.v-OverviewEvent.v-OverviewEvent--allDay.v-OverviewEvent--first.v-OverviewEvent--last {
    border-radius: 0 0px 0px 0
}

.v-CalendarNav-box.v-CalendarNav-month.v-CalendarNav-full {
	font-size: 125% !important;
}

.v-OverviewDay {
    border-right: 2px solid #EEEEEE !important;
}

.v-OverviewDay--odd {
	background: #FAFAFA;   
}
}
