css:
@-moz-document url-prefix("https://www.livecoding.tv/") {
.browse-main {
    background-color: #000;
}
.browse-wrapper {
    background-color: #000;
}
.active {
    background-color: #000;
}
.nav-tabs>li.active>a {
    background-color: #000;
}
.nav-tabs>li.active>a:hover {
    background-color: #000;
}
.browse-sidebar {
    background-color: #000;
}
.browse-page .browse-main-header-updated {
    border-bottom-color: #2574a9;
}
.browse-page .browse-main {
    border-right-color: #2574a9;
}
.browse-page .browse-sidebar--title {
    border-bottom-color: #2574a9;
}
.site-footer--location span {
    color: #2574a9;
}
.site-footer--copyright span {
    color: #2574a9;
}
.site-footer--location i {
    color: #2574a9;
}
.stream-desc-social .share-btn-box {
    background-color: #000;
}
.stream-desc-social .share-btn-box span {
    color: #a62e86;
}
.stream-desc-social .access-source-code {
    background-color: #000;
}
.stream-desc-info--list {
    background-color: #000;
}
.stream-desc-info--list li label {
    color: #a62e86;
}
.stream-desc-info--desc {
    background-color: #000;
}
.stream-desc-info--desc p {
    color: #a62e86;
}
.stream-desc-info--tags {
    background-color: #000;
}
.payment-desc,
.stream-desc {
    background-color: #000;
}
.stream-broadcasts {
    background-color: #000;
}
.stream-broadcasts--title {
    color: #a62e86;
}
.chat-heading,
.message-pane-wrapper,
.message-textarea,
.message-form textarea,
.message-pane {
    background-color: #000;
}
.message {
    color: #a62e86;
}
#candy,
#chat-pane,
#chat-rooms,
.chat-heading,
.message-textarea {
    background-color: #000;
}
.jspDrag {
    background: #a62e86;
}
#chat-rooms .message-form {
    border-top-color: #a62e86;
}
.chat-heading div {
    border-bottom-color: #a62e86;
}
.browse-main {
    border-right-color: #2574a9;
}
.browse-header {
    background-color: #000;
}
.browse-header-main {
    border-right-color: #2574a9;
}
.schedule-list-item {
    background-color: #a62e86;
    border: 0px;
    color: white;
}
.schedule-list-item--desc p a {
    color: white;
}
.schedule-list-item--time span {
    color: white;
}
.schedule-list-item--links a {
    color: #5E97C7;
}
.message-form textarea {
    color: white;
}
}
