css:
@-moz-document domain("habitica.com") {
.task-column {
	padding: 0 0.5em;
	border: none;
}
.toolbar-container::before {
	width: 130px;
}
.toolbar-mobile-nav {
	display: inline-block;
}
.toolbar-nav {
	display: none;
}
.filters .filters-tags>a {
	border: 1px solid #ccc!important;
	/*	border-radius: 1em !important; */
	
	min-width: 100px;
	margin: 0 4px 2px 0;
	text-align: center;
}
.options-menu {
	position: static !important;
}
/*
.options-menu, .options-submenu {
	position: relative !important;
}*/

.hero-stats {
	width: 80%;
}
.party {
	width: auto;
}
.toolbar-container::before {
	image-rendering: auto;
	background-color: transparent !important;
}
.task-filter,
.filters,
.task-column:not(.rewards) .task-options > form > fieldset:nth-last-of-type(1) {
	display: inline-block !important;
}
.background_frigid_peak {
	background: #ffa319;
	/* Old browsers */
	
	background: -moz-radial-gradient(center, ellipse cover, #ffa319 7%, #ff3019 100%);
	/* FF3.6+ */
	
	background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(7%, #ffa319), color-stop(100%, #ff3019));
	/* Chrome,Safari4+ */
	
	background: -webkit-radial-gradient(center, ellipse cover, #ffa319 7%, #ff3019 100%);
	/* Chrome10+,Safari5.1+ */
	
	background: -o-radial-gradient(center, ellipse cover, #ffa319 7%, #ff3019 100%);
	/* Opera 12+ */
	
	background: -ms-radial-gradient(center, ellipse cover, #ffa319 7%, #ff3019 100%);
	/* IE10+ */
	
	background: radial-gradient(ellipse at center, #ffa319 7%, #ff3019 100%);
	/* W3C */
}
.background_ice_cave {
	/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#f85032+0,f16f5c+50,f6290c+51,f02f17+71,e73827+100;Red+Gloss+%231 */
	
	background: #f85032;
	/* Old browsers */
	
	background: -moz-radial-gradient(center, ellipse cover, #f85032 0%, #f16f5c 50%, #f6290c 51%, #f02f17 71%, #e73827 100%);
	/* FF3.6-15 */
	
	background: -webkit-radial-gradient(center, ellipse cover, #f85032 0%, #f16f5c 50%, #f6290c 51%, #f02f17 71%, #e73827 100%);
	/* Chrome10-25,Safari5.1-6 */
	
	background: radial-gradient(ellipse at center, #f85032 0%, #f16f5c 50%, #f6290c 51%, #f02f17 71%, #e73827 100%);
	/* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}
.background_snowy_pines {
	/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#959595+0,0d0d0d+46,010101+50,0a0a0a+53,4e4e4e+76,383838+87,1b1b1b+100;Black+Gloss+Pipe */
	
	background: #959595;
	/* Old browsers */
	
	background: -moz-linear-gradient(top, #959595 0%, #0d0d0d 46%, #010101 50%, #0a0a0a 53%, #4e4e4e 76%, #383838 87%, #1b1b1b 100%);
	/* FF3.6-15 */
	
	background: -webkit-linear-gradient(top, #959595 0%, #0d0d0d 46%, #010101 50%, #0a0a0a 53%, #4e4e4e 76%, #383838 87%, #1b1b1b 100%);
	/* Chrome10-25,Safari5.1-6 */
	
	background: linear-gradient(to bottom, #959595 0%, #0d0d0d 46%, #010101 50%, #0a0a0a 53%, #4e4e4e 76%, #383838 87%, #1b1b1b 100%);
	/* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}
