css:
@-moz-document domain("127.0.0.1"), domain("localhost"), domain("app.plex.tv") {
/* ================= settings ================== */
/* i really want this to be global */
html, body, body.thumbnail
{ background-color: #21333E;
    color: #A0A0A0
}
.header, .footer
{ background-color: #121A23;
    color: #EBF0F5;
    border: 0px
}

.header a
{ color: #EBF0F5;
}

.footer a
{ color: #EBF0F5;
}

.help-block
{ color: #B7C4CE;
}

.text-success
{ color: #23F723;
}

.text-danger
{ color: #E32B2B;
    font-weight: 600
}
/*FF1919*/

.media-poster
{ border-radius: 3px;
    background-color: #273943;
}

.filter-bar
{ background-color: #17212C;
    border-top: 1px solid #2d2d2d;
    border-bottom: 1px solid #0e0e0e;
}
.background-container {
    background-color: #17212C;
}

/* code below removes blurred backgoung in Web App ver 3.0.0 - thanks to Plex forum member Shadowfied */
#plex > div.background-container > div > div > div:nth-child(2){
    background: #21333E !important;
}



/* ==================== Navigation Bar and Nav Bar Elements =============== */

.nav-bar
{ background-color: #17212C;
    box-shadow: 0 0 0 3px rgba(0, 0, 0, .2);
}

.nav-bar-nav>li>a
{ padding-top: 0;
    padding-bottom: 0;
    line-height: 60px;
    color: #D9E6E6;
}

.dropdown-menu
{ min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    background-color: #17212c;
    border: 1px solid #222;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: 3px;
    box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
}

.dropdown-menu>li>a
{ display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: 400;
    line-height: 1.71428571;
    color: #B7C4CE;
    white-space: nowrap;
}

.dropdown-menu>li>a:hover
{ color: white;
    background-color: rgba(63, 70, 77, .31);
}

.dropdown-poster-toggle .media-poster.placeholder-friend
{ background-color: #EBF0F5;
    -webkit-box-shadow: inset 0 0 0 2px yellow;
    box-shadow: inset 0 0 0 1px #f9be03;
}

.show-side-bar.show-action-bar .filter-bar-fixed
{ left: 310px;
}

.show-side-bar.show-action-bar .breadcrumb-bar, .show-side-bar.show-action-bar .filter-bar-fixed
{ left: 300px;
}

.side-bar
{ width: 240px;
    padding: 0 5px 5px;
    overflow-x: visible;
}

.dashboard-section-list-item .section-icon
{ color: #B7C4CE;
    background-color: inherit;
}

.dashboard-section-list-item:hover
{ background-color: rgba(63, 70, 77, .175);
}
.MenuContainer-menu-27j2V {
    padding: 8px 0;
    width: 170px;
    border-radius: 4px;
    background-color: #17212c;
    box-shadow: 0 4px 10px rgba(0,0,0,.35);
}
/* ======================== Metadata Editor  AND Form Control Elements ======================*/

.modal-header
{ background-color: #2A3139;
    border-top: 1px solid #2d2d2d;
    border-bottom: 1px solid #0e0e0e;
}

.modal-content
{ background-color: #121A23;
}

.modal-footer
{ background-color: #2A3139;
    border-top: 1px solid #2d2d2d;
    border-bottom: 1px solid #0e0e0e;
}

.edit-lock-addon
{ color: #121A23;
    background-color: #2A3139;
    border-color: #2A3139;
}

.form-control
{ color: #eee;
    background-color: rgba(92, 109, 114, .62);
}

.selectize-input.full
{ background-color: rgba(92, 109, 114, .62);
}

.selectize-group .form-control.selectize-control.single .selectize-input input
{ color: #555;
    width: 100%;
    position: absolute;
    left: 14px;
    cursor: text;
}

div.input-group
{ background-color: rgba(92, 109, 114, .62);
}

div.selectize-input.items.not-full
{ background-color: rgba(92, 109, 114, .62);
}

.form-control-search
{ height: 29px;
    padding: 2px 28px 2px 32px;
    background-color: rgba(92, 109, 114, .92);
    border-radius: 10px !important
}

.files ul li
{ color: #EEE;
    background-color: rgba(92, 109, 114, .92);
}

div.selectize-dropdown.multi.form-control.lockable-field
{ color: #EEE;
    background-color: rgba(63, 70, 77, .99);
}

ul.media-info-file-list.well
{ background-color: #2A3139;
}
/* ========================== Server Settings Page Elements ============================ */

.server-account-container>.server-account-info .server-account-username
{ overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    color: #23F723;
}

.check-for-updates-container>.install-info-label .success-icon
{ margin-right: 2px;
    color: #23F723;
}

.server-settings-nav-list-item.success .success
{ display: inline-block;
    color: #23F723;
}

.nav-cards>li>.card
{ background-color: #17212c;
    border-bottom: 1px solid #061219;
}

.nav-cards>li>a.card:focus
{ background-color: #17212c;
    border-bottom: 1px solid #061219;
}

.nav-cards>li>a.card:hover
{ color: #ccc;
    background-color: rgba(63, 70, 77, .175)
}

.alert-list-container
{ background-color: #17212c;
}
/*================= Drop Down Menu Elements ========================*/

a.delete-btn.has-danger:hover
{ color: yellow;
    background-color: red;
}

a.delete-btn.has-danger
{ color: red;
    background-color: yellow;
}

a.delete-btn.has-danger:hover:before
{ color: yellow;
    background-color: red;
    content: '!!  '
}

a.delete-btn.has-danger:hover:after
{ color: yellow;
    background-color: red;
    content: '  !!'
}

.nav-pills>li.active>a
{ background-color: #17212C;
    border-top: 1px solid #2d2d2d;
    border-bottom: 1px solid #0e0e0e;
}

.nav-pills-sm>li.active>a
{ background-color: #17212C;
    padding-top: 4px;
    padding-bottom: 4px;
}
/*====================== Buttons ==========================*/

.btn-default
{ border-color: #ccc;
    color: #fff;
    text-shadow: 0 -1px 1px rgba(0, 0, 0, .4), 0 0 15px rgba(0, 0, 0, .2);
    background-color: #21333E;
}

.btn-default:hover
{ border-color: #ccc;
    color: #fff;
    text-shadow: 0 -1px 1px rgba(0, 0, 0, .4), 0 0 15px rgba(0, 0, 0, .2);
    background-color: #4D5C65;
}

.btn-sm
{ padding: 2px 14px;
    font-size: 12px;
    line-height: 1.5;
    border-radius: 3px;
    background-color: #21333E;
}

button.server-sign-out-btn.btn.btn-default.btn-loading
{ background-color: #FF9900;
}

button.server-sign-in-btn.btn.btn-primary.btn-loading:hover
{ background-color: #23F723;
}

button.server-sign-out-btn.btn.btn-default.btn-loading:hover
{ background-color: red;
}

.btn-gray
{ padding: 0;
    color: #B7C4CE;
    background-color: #B7C4CE;
    text-shadow: none;
    background: 0 0;
    border: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
}

button.remote-access-disable-btn.btn.btn-sm.btn-default
{ color: #FFF;
    background-color: #e59029;
}

button.remote-access-disable-btn.btn.btn-sm.btn-default:hover
{ background-color: #FF1919;
}

button.remote-access-enable-btn.btn.btn-sm.btn-primary
{ color: #FFF;
    background-color: #e59029;
}

button.remote-access-enable-btn.btn.btn-sm.btn-primary:hover
{ color: #FFF;
    background-color: #198D19;
}
/* ================= Tables and Cards Elements =================== */

.card-handle
{ display: inline-block;
    width: 26px;
    margin-right: 10px;
    font-size: 12px;
    color: #eee;
    text-align: center;
    background-color: #17212C;
}

.card-sortable
{ background-color: #17212C;
}

.card-sortable:hover
{ background-color: rgba(63, 70, 77, .175)
}

.card-sortable:focus
{ background-color: #5C6D72;
    border-top: 1px solid #373737;
}

.card
{ background-color: #17212C;
    border-top: 1px solid #2d2d2d;
    border-bottom: 1px solid #0e0e0e;
}

.card-sortable:hover .card-handle, .card-sortable:focus .card-handle
{ background-color: #ACAFB3;
}

.ul.list.contributer-list
{ background-color: #F2F2F2 !important
}

.card-list
{ background-color: #16272A;
}

a.card:hover, a.card:focus
{ background-color: rgba(63, 20, 77, .175);
}
/* ========================= Miscellanious ============================ */

.remote-access-status-icon
{ float: left;
    margin-right: 20px;
    font-size: 60px;
    color: #358294;
}

.remote-access-disable-warning-icon
{ float: left;
    margin-top: 10px;
    margin-right: 15px;
    font-size: 24px;
    color: #F5D907;
}

.user-poster
{ background-color: #358294;
    -webkit-box-shadow: inset 0 0 0 2px yellow;
    box-shadow: inset 0 0 0 1px black;
    border-radius: 3px
}

.check-for-updates-container.has-release-notes>.release-notes-btn
{ border-radius: 2px;
}

.label-primary
{ border-radius: 4px;
}

.device-icon-default
{ background-color: #fbaf23;
    border: 3px solid #ffff00
}

.glyphicon circle-question-mark
{ color: green;
    background-color: blue;
}

.user-playlist-full .plex-icon
{ color: #358294;
}

.user-playlist-full .plex-icon:hover
{ color: #ff8a00;
}

.user-playlist-help
{ color: #358294
}

.user-playlist-help:hover
{ color: #ff8a00;
}

.queue-help-container .row .queue-email.disabled
{ background-color: rgba(92, 109, 114, .62);
}

.label-default
{ background-color: rgba(92, 109, 114, .62);
    border-radius: 5px!important
}

.label-default:hover
{ background-color: #86B4BF}

}
